                     (fastsatt(samletAvskrivning.beloep) erLik 0)
                 }


             hvis(harNettoleieinntektVedUtleieUtenBruttoinntektEllerKostnader)
             {
                 betjeningsstrategiSettesTil(ikke, faktiskFeil)
             }
         }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Kontrollen gir utslag dersom det er differanse på mer enn 1 mill mellom grunnlag og fastsatt rentekostnad
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(konto)
            .utslagFor {
                val differanseRentekostnad =
                    differanse(
                        fastsatt(andelAvPaaloepteRenter.beloepSomTall),
                        grunnlag(andelAvPaaloepteRenter.beloepSomTall)
                    )

                hvis(ikke(harInnsending) og (differanseRentekostnad erStørreEnn 1_000_000)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Kontrollen gir utslag dersom det er differanse på mer enn 1 mill mellom grunnlag og fastsatt renteinntekt
        utgåtteSkattemeldingFelter {
