                val endretBoligMedEierandelUnder100Prosent = hvorMinstEnForekomst {
                  (fastsatt(eierandel) erStørreEnn 0) og 
                  (fastsatt(eierandel) erMindreEnn 100) og
                  ((fastsatt(byggeaar) erUlik grunnlag(byggeaar)) eller
                   (fastsatt(boligensAreal) erUlik grunnlag(boligensAreal)) eller
                   (fastsatt(boligtype) erUlik grunnlag(boligtype)) eller
                   (fastsatt(dokumentertMarkedsverdiForBolig.beloep) erUlik grunnlag(dokumentertMarkedsverdiForBolig.beloep)))
            }

            hvis(endretBoligMedEierandelUnder100Prosent) 
              {
                betjeningsstrategiSettesTil(
                    ikke, merknadInformasjon)
              }
            }
    }

