sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) 
    .importer(SKATTEFRIE_UNNTAK) 
     { 
       (D_skattefrieUnntak) ->

        val skattepliktforekomster = skattepliktGjeldende
          .forForekomsterAv { skatteplikt }      

       val erIkkeSjoeEllerSokkel = skattepliktforekomster
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .harIngenForekomsterDer { (type erLik "skattepliktEtterPetroleumsskatteloven") eller (type erLik "skattepliktSomSjoemann") }

        val harBegrensetSkatteplikt = skattepliktforekomster
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" } 

        val periodelagerforekomster = periodelager  

        val harOppholdUnder183Dager = periodelagerforekomster
            .forForekomsterAv { totalPerioder }
            .hvorMinstEnForekomst {
                (antallDagerMedArbeidsforholdsperioder erMindreEnn 183) og
                    (antallDagerMedArbeidsforholdsperioder erStørreEnn 0)
            }

        val harEntreprise = periodelagerforekomster
            .forForekomsterAv { perioder }
            .forForekomsterAv { arbeidsforholdsperiode }
            .hvorMinstEnForekomst { oppdrag.kontraktstype erLik "selvstendigAvOppdragstaker" }

        val alleArbeidsforholdHarFastDriftstedLikNei = periodelagerforekomster
            .forForekomsterAv { perioder }
            .forForekomsterAv { arbeidsforholdsperiode }
            .filtrer { informasjonskilde.kilde erLik "oppdragsregister" }
            .hvorAlleForekomster { oppdrag.erFastDriftssted erLik false }   

        // kontrollen gir utslag dersom skattyter har lønn som ikke er skattefri
        // dersom skattyter er uten forhold sjø og sokkel,
        // og skattyter har begrenset skatteplikt
        // og skattyters har oppdrag av type entreprise u/ fast driftssted under 183 dager 
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv{arbeidsgiver}
            .utslagFor {

            val harSkattepliktigLoenn = forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
                .hvorMinstEnForekomst { (fastsatt(beloep.beloep) erStørreEnn 0) og 
                                        (fastsatt(beloep.trygdeEllerSkattemessigUnntak).finnesBlant(D_skattefrieUnntak))}

            hvis(harSkattepliktigLoenn og 
                 erIkkeSjoeEllerSokkel og 
                 harBegrensetSkatteplikt og
                 harOppholdUnder183Dager og
                 harEntreprise og 
                 alleArbeidsforholdHarFastDriftstedLikNei) {
                betjeningsstrategiSettesTil(ikke, merknadStandard)
            }
        }
        }

