sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret gevinst på fondskonto med differanse mellom grunnlag og fastsatt på 50 000 eller mer
        // Gir merknad i SME
        skattemelding
            .forForekomsterAv{fondskonto}
            .filtrer{(grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloep) er oppgitt) eller
                     (grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedel.beloep) er oppgitt)}
            .utslagFor {
                val differanseGevinstAksjedel = summer{differanse(
                        grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloep),
                        fastsatt(skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloep))
                }

                val differanseGevinstRentedel = summer{differanse(
                        grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedel.beloep),
                        fastsatt(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedel.beloep))
                }

                hvis((differanseGevinstAksjedel + differanseGevinstRentedel) erStørreEnnEllerLik 50_000) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert formue på fondskonto
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv{fondskonto}
            .filtrer {(grunnlag(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloep) er oppgitt) eller 
                      (grunnlag(formuesverdiForKontanterMv.beloep) er oppgitt)
            }
            .utslagFor {
                val differanseFormueVerdi = verdiFor{
                    grunnlag(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloep) - 
                    fastsatt(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloep)}

                val differanseFormueKontanter = verdiFor{
                    grunnlag(formuesverdiForKontanterMv.beloep) - 
                    fastsatt(formuesverdiForKontanterMv.beloep)}

                val formueOgKontanterErStoerreEnnBeloepsgrense = hvorMinstEnForekomst{(differanseFormueVerdi + differanseFormueKontanter) erStørreEnnEllerLik 500_000}

                hvis(ikke(harInnsending) og
                     formueOgKontanterErStoerreEnnBeloepsgrense
                    ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }

