        // kontrollen gir merknad i SME
       skattemelding
            .forForekomsterAv{fondskonto}
            .filtrer {
                (grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloep) er oppgitt) eller 
                (grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedel.beloep) er oppgitt)
            }
            .utslagFor {
                val differanseGevinstAksjedel = verdiFor{
                        grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloep) - 
                        fastsatt(skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloep)}

                val differanseGevinstRentedel = verdiFor{
                        grunnlag(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedel.beloep) - 
                        fastsatt(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedel.beloep)}

                val diffGevinstAksjeOgRentedelStoerreEnnBelopesgrense = hvorMinstEnForekomst{(differanseGevinstAksjedel + differanseGevinstRentedel) erStørreEnnEllerLik 50_000}

                val harVedleggAksjedel = hvorMinstEnForekomst{skattepliktigGevinstVedRealisasjonAvOgUttakFraAksjedel.beloep har vedleggPåFelt}

