sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret underskudd til fremføring
        // ikke utslag når dokumentasjon er vedlagt
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(fremfoertUnderskuddFraTidligereAar)
            .filtrer { grunnlag(fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0 }
            .utslagFor {
                val harEndretFremfoerbartUnderskudd = hvorMinstEnForekomst { grunnlag(fradragsberettigetBeloep.beloepSomTall) erUlik 
                                                                             fastsatt(fradragsberettigetBeloep.beloepSomTall) }

                val harVedlegg = forForekomsterAv(beloepUtenHensynTilValgtPrioritertFradragstype)
                   .hvorMinstEnForekomst { fastsatt(beloepSomTall) har vedlegg }

                hvis(harEndretFremfoerbartUnderskudd og 
                      ikke(harVedlegg) og
                      ikke(harInnsending)    
                    ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon)
                }
            }
        }
    }

