        val sergIdFraKontrollisteFritidseiendommerUtenBygninger = hentKontrolliste(
            "Fritidseiendommer uten bygninger i matrikkel", partsnr) 
            .forForekomsterAv { rad }
            .verdierFor{sekundærIdentifikator}


        // Kontrollen gir utslag på fritidseiendommer ("selveidFritidseiendom", "egenFritaksbehandletFritidseiendom", "regnskapsbehandletFritidseiendom") 
        // hvor skattyter har eienandel større enn 0, 
        // men mangler formuesverdi
        // gir IKKE utslag dersom skattyter og eiendommen ligger i listen over fritidseiendommer uten bygninger
        // Gir merknad i SME
        skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer{fastsatt(sergEiendomsidentifikator).finnesIkkeBlant(sergIdFraKontrollisteFritidseiendommerUtenBygninger)}
