         val sergIdFraKontrollisteFritidseiendomMedGarasjeEllerNaust = hentKontrolliste(
            "Fritidseiendommer med garasje eller naust i matrikkel", partsnr) 
            .forForekomsterAv { rad }
            .verdiFor{sekundærIdentifikator}       

        val partErDoed = partInfo
            .forForekomsterAv { partInfo }
            .hvorMinstEnForekomst { erDoed erLik true }    

        // Kontrollen gir utslag på eiendommer av type fritidseiendom i preutfylt skattemelding som ligger i kontrolliste med fritidseiendommer med garasje eller naust
        // gir kun utslag hvis fastsatt eierandel er større enn 0
        // gir ikke utslag hvis skattyter er død
        // Gir merknad i SME
        skattemelding
            .forForekomsterAv{fastEiendom}
            .filtrer{fastsatt(sergEiendomsidentifikator) erLik sergIdFraKontrollisteFritidseiendomMedGarasjeEllerNaust}
            .forForekomsterAv{fastEiendomSomFormuesobjekt}
            .utslagFor{

              val skattyterHarEierandelStoerreEnn0PaaFritidseiendomMedGarasjeEllerNaust = hvorMinstEnForekomst{fastsatt(eierandel) erStørreEnn 0}    


              hvis(skattyterHarEierandelStoerreEnn0PaaFritidseiendomMedGarasjeEllerNaust og
                  ikke(partErDoed)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadInformasjon
                    )
                }
            }
    }

