sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val oppgittEndringsaarsakIkkeErOverførtFraEktefelle = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(konto, aarsakTilEndring))
            .harIngenForekomsterDer { aarsak erLik "overfortTilFraEktefelle" }
        }

        // Kontrollen gir utslag dersom skattyter har oppgitt gjeldsrenter utover det som er innrapportert av tilbyder
        // AB-test: skattytere som har tallet 1, 3, 5, 7 eller 9 i 7.siffer i partsnummeret
        // Skattepliktigs andel av gjeld og renter må være 100 % ifølge GLD 
        // og har ikke oppgitt at det er overført fra ektefelle/samboer
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(konto)
            .filtrer {
                grunnlag(andelAvPaaloepteRenter.beloepSomTall) er oppgitt
            }
            .utslagFor {
                val harDifferanseRentekostnad = filtrer {
                    (grunnlag(prosentandelAvGjeldOgPaaloepteRenter) er oppgitt) og
                        (grunnlag(prosentandelAvGjeldOgPaaloepteRenter) erLik 100)
                }
                    .hvorMinstEnForekomst {
                        (fastsatt(andelAvPaaloepteRenter.beloepSomTall) -
                            grunnlag(andelAvPaaloepteRenter.beloepSomTall)) erStørreEnn 0
                    }

                hvis(
                    ikke(harInnsending) og (partsnr.tegnFraIndeks(7).finnesBlant("1", "3", "5", "7", "9")) og
                        harDifferanseRentekostnad og oppgittEndringsaarsakIkkeErOverførtFraEktefelle
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadBekreft)
                }
            }
        }
    }

