sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harKravPaaSaerfradragStoreSykdomsutgifter = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(saerfradragForStoreSykdomskostnader)
            .hvorMinstEnForekomst { grunnlag(rettTilAaKreveSaerfradrag) erLik true }
        }

        // Kontrollen gir utslag og merknad når skattyter har lagt til andre kostnader knyttet til arbeidsinntekt eller annen inntekt
        // med beskrivelse som INNEHOLDER ordene: operasjon, kjeve, dent, sykdom, sykehus, fysio, medisin, medical,
        // diabetes, psyk, tann, helse eller osteopat
        // med beskrivelse som har NØYAKTIG ordene: tenner eller lege
        // Skal ikke gi utslag dersom de har krav på store sykdomsutgifter
        // Skattepliktig krever > 0 i feltet "Andre kostnader knyttet til arbeidsinntekt" samt andre kostnader
        // knyttet til arbeidsinntekt + øvrige fradrag som er med og overstyrer minstefradrag > minstefradrag
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(minstefradragOgKostnaderKnyttetTilArbeidOgAnnenInntekt)
            .utslagFor {
                val sumAndreKostnaderArbeidsinntektStørreEnnMinstefradrag =
                    fastsatt(fradragForKostnaderKnyttetTilArbeidSomGruppe.beloepUtenHensynTilValgtPrioritertFradragstype.beloepSomTall) -
                        fastsatt(minstefradragIInntektSomGruppe.beloepUtenHensynTilValgtPrioritertFradragstype.beloepSomTall)
                val harAndreKostnaderAnnenInntekt =
                    fastsatt(fradragForKostnaderKnyttetTilAnnenInntekt.fradragsberettigetBeloep.beloepSomTall)
                val harBeskrivelseSomInneholderOrdUnderAnnenInntekt = forForekomsterAv(kostnad)
                    .hvorMinstEnForekomst {
                        (fastsatt(kostnadstype) erLik "andreKostnaderKnyttetTilAnnenInntekt") og
                            (beloepSomGruppe.beloepSomTall erStørreEnn 0) og
                            (
                                (beskrivelse.inneholder("operasjon")) eller
                                    (beskrivelse.inneholder("kjeve")) eller
                                    (beskrivelse.inneholder("dent")) eller
                                    (beskrivelse.inneholder("sykdom")) eller
                                    (beskrivelse.inneholder("sykehus")) eller
                                    (beskrivelse.inneholder("fysio")) eller
                                    (beskrivelse.inneholder("medisin")) eller
                                    (beskrivelse.inneholder("medical")) eller
                                    (beskrivelse.inneholder("diabetes")) eller
                                    (beskrivelse.inneholder("psyk")) eller
                                    (beskrivelse.inneholder("tann")) eller
                                    (beskrivelse.inneholder("helse")) eller
                                    (beskrivelse.inneholder("osteopat"))
                                )
                    }
                val harBeskrivelseMedNøyaktigOrdUnderAnnenInntekt = forForekomsterAv(kostnad)
                    .hvorMinstEnForekomst {
                        (fastsatt(kostnadstype) erLik "andreKostnaderKnyttetTilAnnenInntekt") og
                            (beloepSomGruppe.beloepSomTall erStørreEnn 0) og
                            (
                                (beskrivelse.erLik("tenner")) eller
                                    (beskrivelse.erLik("lege"))
                                )
                    }
                val harBeskrivelseSomInneholderOrdeUnderArbeidsinntekt = forForekomsterAv(kostnad)
                    .hvorMinstEnForekomst {
                        (fastsatt(kostnadstype) erLik "andreKostnaderKnyttetTilArbeidsinntekt") og
                            (beloepSomGruppe.beloepSomTall erStørreEnn 0) og
                            (
                                (beskrivelse.inneholder("operasjon")) eller
                                    (beskrivelse.inneholder("kjeve")) eller
                                    (beskrivelse.inneholder("dent")) eller
                                    (beskrivelse.inneholder("sykdom")) eller
                                    (beskrivelse.inneholder("sykehus")) eller
                                    (beskrivelse.inneholder("fysio")) eller
                                    (beskrivelse.inneholder("medisin")) eller
                                    (beskrivelse.inneholder("medical")) eller
                                    (beskrivelse.inneholder("diabetes")) eller
                                    (beskrivelse.inneholder("psyk")) eller
                                    (beskrivelse.inneholder("tann")) eller
                                    (beskrivelse.inneholder("helse")) eller
                                    (beskrivelse.inneholder("osteopat"))
                                )
                    }
                val harBeskrivelseMedNøyaktigOrdUnderArbeidsinntekt = forForekomsterAv(kostnad)
                    .hvorMinstEnForekomst {
                        (fastsatt(kostnadstype) erLik "andreKostnaderKnyttetTilArbeidsinntekt") og
                            (beloepSomGruppe.beloepSomTall erStørreEnn 0) og
                            (
                                (beskrivelse.erLik("tenner")) eller
                                    (beskrivelse.erLik("lege"))
                                )
                    }
                hvis(
                    ikke(harInnsending) og
                        ikke(harKravPaaSaerfradragStoreSykdomsutgifter) og
                        (((sumAndreKostnaderArbeidsinntektStørreEnnMinstefradrag erStørreEnn 0) og (harBeskrivelseSomInneholderOrdeUnderArbeidsinntekt
                            eller harBeskrivelseMedNøyaktigOrdUnderArbeidsinntekt)) eller
                            ((harAndreKostnaderAnnenInntekt erStørreEnn 0) og (harBeskrivelseSomInneholderOrdUnderAnnenInntekt eller
                                harBeskrivelseMedNøyaktigOrdUnderAnnenInntekt)))
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

