            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsgiver, naeringsinntektMv))
            .filtrer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }
            .utslagForFørste {

                val harLoenn = hvorMinstEnForekomst { arbeidsgiversNavn er oppgitt }

                val harNaering = hvorMinstEnForekomst { naeringstype er oppgitt }

                val harRegistrertTrygdefritakPensjonsdelLoenn =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt) og
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                                )
                                    )
                        }

                val harRegistrertTrygdefritakPensjonsdelNaeringsinntekt = hvorMinstEnForekomst {
                    (fastsatt(naeringsinntektTilordnetInnehaver.beloepSomTall) er oppgitt) og
                        (fastsatt(naeringsinntektTilordnetInnehaver.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                        )
                            )
                }

                val harRegistrertTrygdefritakPensjonsdelNaeringsunderskudd = hvorMinstEnForekomst {
                    (fastsatt(underskuddTilordnetInnehaver.beloepSomTall) er oppgitt) og
                        (fastsatt(underskuddTilordnetInnehaver.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel"
                        )
                            )
                }

                hvis(
                    (verdiDekningFraKontrolliste.finnesBlant(
                        "FTL_2-9_1_ledd_a",
                        "FTL_2-9_2_ld_jfr_1a",
                        "FTL_2-7a_2_ledd_b",
                        "FTL_2-7_3_ledd_b"
                    )) og
                        (alleVerdierFraKontrolliste.antall erStørreEnn 0) og
                        ((harLoenn og ikke(harRegistrertTrygdefritakPensjonsdelLoenn)) eller
                            (harNaering og ikke(harRegistrertTrygdefritakPensjonsdelNaeringsinntekt) og
                                ikke(harRegistrertTrygdefritakPensjonsdelNaeringsunderskudd)))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadITopp
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val kontrollisteOppslag = hentKontrolliste(
            "kontrolliste - Medlemskap folketrygden",
            listeAv(partsnr))
            .forForekomsterAv { verdi }

        val alleVerdierFraKontrolliste = kontrollisteOppslag
            .alleVerdier

        val verdiDekningFraKontrolliste = kontrollisteOppslag
            .verdierForListe { tekstVerdier("verdi8", "verdi9", "verdi10", "verdi11", "verdi12", "verdi13") }

        //kontrollen gir utslag og merknad dersom skattyters partsnr finnes i kontrollisten
        //og de i kontrollisten har dekning = "FTL_2-9_1_ledd_b" (Nav Internasjonalt har fattet et vedtak om frivillig/pliktig medlemskap i norsk folketrygd pensjonsdel)
        //og skattyter IKKE har registrert trygdefritak for helsedel
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsgiver, naeringsinntektMv))
            .filtrer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }
            .utslagForFørste {
                val harLoenn = hvorMinstEnForekomst { arbeidsgiversNavn er oppgitt }

                val harNaering = hvorMinstEnForekomst { naeringstype er oppgitt }

                val harRegistrertTrygdefritakHelsedelLoenn =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt) og
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattepliktigUtenTrygdeavgiftspliktForHelsedel"
                                )
                                    )
                        }

                val harRegistrertTrygdefritakHelsedelNaeringsinntekt = hvorMinstEnForekomst {
                    (fastsatt(naeringsinntektTilordnetInnehaver.beloepSomTall) er oppgitt) og
                        (fastsatt(naeringsinntektTilordnetInnehaver.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                            "skattepliktigUtenTrygdeavgiftspliktForHelsedel"
                        )
                            )
                }

                val harRegistrertTrygdefritakHelsedelNaeringsunderskudd = hvorMinstEnForekomst {
                    (fastsatt(underskuddTilordnetInnehaver.beloepSomTall) er oppgitt) og
                        (fastsatt(underskuddTilordnetInnehaver.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                            "skattepliktigUtenTrygdeavgiftspliktForHelsedel"
                        )
                            )
                }


                hvis(
                    (verdiDekningFraKontrolliste.finnesBlant("FTL_2-9_1_ledd_b")) og
                        (alleVerdierFraKontrolliste.antall erStørreEnn 0) og
                        ((harLoenn og ikke(harRegistrertTrygdefritakHelsedelLoenn)) eller
                            (harNaering og ikke(harRegistrertTrygdefritakHelsedelNaeringsinntekt) og
                                ikke(harRegistrertTrygdefritakHelsedelNaeringsunderskudd)))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadITopp
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val erIkkeSjoeEllerSokkel = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .harIngenForekomsterDer { (type erLik "skattepliktEtterPetroleumsskatteloven") eller (type erLik "skattepliktSomSjoemann") }

        // kontrollen gir utslag dersom skattyter er uten forhold sjø og sokkel og har valgt "ikke skattepliktig" på lønnsinntekt,
        // og skattyters partsnr finnes i kontrollisten - de ligger i kontrollisten hvis SP har oppdrag med "Ja" på FD (fast driftssted), er Innleie i OAR og har NT1 skjema
        // kontrollen gir merknad i SME
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {

            val harOppgittSkattefriLoenn = forForekomsterAv(arbeidsgiver)
                .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                .hvorMinstEnForekomst {
                    (
                        fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                            "skattefriMedTrygdeavgiftsplikt",
                            "skattefriKildeskattPaaPensjon",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                        )
                        ) og
                        (
                            ikke(
                                grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                    beloepSomGruppe.trygdeEllerSkattemessigUnntak
                                )
                            )
                            )
                }

            val partsnrIidentifikatorIkontrolliste = finnesIKontrolliste(

                "kontrolliste - Ikke skattepliktig loenn med NT1 skjema",
                listeAv(partsnr)
            )

            hvis(erIkkeSjoeEllerSokkel og harOppgittSkattefriLoenn og partsnrIidentifikatorIkontrolliste) {
                betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
            }
        }
        }
    }

sakstype(Personlig)
