sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når antall måneder drevet i år er lavere enn 0 eller høyere enn 12
        // kontrollen gir merknad i valideringstjenesten
        naeringsspesifikasjon
            .forForekomsterAv { fordeltBeregnetPersoninntekt }
            .utslagFor {
                hvis((verdiFor { antallMaanederDrevetIAar } erStørreEnn 12) eller (verdiFor { antallMaanederDrevetIAar } erMindreEnn 0)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
