        val harFeilFinansinntektTypeForIngenRegnskapspliktEllerBegrensetRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv { resultatregnskap.finansinntekt }
            .forForekomsterAv { inntekt }
            .hvorMinstEnForekomst {
                type.finnesBlant("8030", "8080")
            }

        val harFullRegnskapspliktEllerIngenRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv { virksomhet }
            .hvorMinstEnForekomst { regnskapspliktstype.finnesBlant("ingenRegnskapsplikt", "fullRegnskapsplikt") }

        val harFeilFinansinntektTypeForFullRegnskapspliktEllerIngenRegnskapsplikt = naeringsspesifikasjon
            .forForekomsterAv { resultatregnskap.finansinntekt }
            .forForekomsterAv { inntekt }
            .hvorMinstEnForekomst {
                type erLik "8091"
            }

        // kontrollen gir utslag dersom skattepliktig har regnskapsplikttype ingen regnskapsplikt, begrenset regnskapsplikt eller full regnskapsplikt
        // sammen med minst en av de nevnte typene av finansinntekt
        naeringsspesifikasjon.utslagFor {
            hvis(
                harGyldigVirksomhetstype og
                    (harIngenRegnskapsplikt og harFeilFinansinntektTypeForIngenRegnskapsplikt) eller
                    (harIngenRegnskapspliktEllerBegrensetRegnskapsplikt og harFeilFinansinntektTypeForIngenRegnskapspliktEllerBegrensetRegnskapsplikt) eller
