            //kontrollen gir utslag dersom skattepliktig mangler kostnadssjablong eller rapporterer feil verdi i feltet for kostnadssjablong
            //kontrollen avviser innsending

    utgåtteSkattemeldingFelter {
        skattemeldingUtgått
        .forForekomsterAv(formuesgrunnlagSkogeiendomINorge)
        .utslagFor {
            val manglerKostnadssjablong = harIngenForekomsterDer {
                (fastsatt(kostnadssjablong) er oppgitt) eller (fastsatt(anvendtKostnadssjablong) er oppgitt)
            }
            val feilKorrigertKostnadsjablong = hvorMinstEnForekomst {
                (fastsatt(korrigertKostnadssjablong) er oppgitt) og
                    (fastsatt(korrigertKostnadssjablong).finnesIkkeBlant("1", "2", "3"))
            }

            hvis(
                manglerKostnadssjablong eller feilKorrigertKostnadsjablong eller 
              	((fastsatt(kostnadssjablong) er oppgitt) og fastsatt(kostnadssjablong).finnesIkkeBlant("1", "2", "3")) eller 
                ((fastsatt(anvendtKostnadssjablong) er oppgitt) og (fastsatt(anvendtKostnadssjablong).finnesIkkeBlant("1", "2", "3")))
            )
            {
                betjeningsstrategiSettesTil(
                    ikke, faktiskFeil
                )
            }
        }
    }
    }

