        .filtrer{fastsatt(eiendomstype) erLik "skogeiendomINorge"}
        .hvorMinstEnForekomst{(fastsatt(eiendomstype) erLik "skogeiendomINorge") og
                              (fastsatt(kostnadssjablong) ikkeEr oppgitt) og 
                              (fastsatt(anvendtKostnadssjablong) ikkeEr oppgitt) og 
                              (fastsatt(korrigertKostnadssjablong) ikkeEr oppgitt)} 

    val feilKorrigertKostnadsjablongUP =  skattemeldingUpersonlig
    	.forForekomsterAv { fastEiendom }
        .forForekomsterAv{fastEiendomSomFormuesobjekt}
        .filtrer{fastsatt(eiendomstype) erLik "skogeiendomINorge"}
