                              ((fastsatt(korrigertKostnadssjablong) er oppgitt) og (fastsatt(korrigertKostnadssjablong).finnesIkkeBlant("1","2","3")))}

     val feilKorrigertKostnadsjablongSDF =  selskapsmeldingSdf
    	.forForekomsterAv { fastEiendom }
