sakstype(Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) {

     val partsnrUp = skattepliktUpersonligGjeldende
        .verdiFor { skattepliktOgOpplysningerOmSkattesubjekt.partsnummer }

     val partsnrSDF = opplysningspliktSdfGjeldende
        .verdiFor{opplysningspliktOgOpplysningerOmSelskapMedDeltakerfastsetting.partsnummer}         

     val erIkkeBoersnotertUp = skattemeldingUpersonlig
        .forForekomsterAv { opplysningOmSkattesubjekt }
        .harIngenForekomsterDer { fastsatt(erBoersnotert) erLik true}

     val erIkkeBoersnotertSDF = selskapsmeldingSdf
        .forForekomsterAv { opplysningOmSelskapMedDeltakerfastsetting }
        .harIngenForekomsterDer { fastsatt(nokusErBoersnotert) erLik true}       

     val harIkkeBoligOgEiendomUP = skattemeldingUpersonlig
        .forForekomsterAv{fastEiendom}
      	.harIngenForekomsterDer{fastEiendomSomFormuesobjekt er oppgitt}

     val harIkkeBoligOgEiendomSDF = selskapsmeldingSdf
        .forForekomsterAv{fastEiendom}
      	.harIngenForekomsterDer{fastEiendomSomFormuesobjekt er oppgitt}    

       // Kontrollen gir utslag dersom selskapet(UP/SDF) har beløp i Balanseverdi for anleggsmidler på konto 1105, 1115, 1150, 1160 eller 1180
       // AB-test på skattytere som har tallet 0, 2, 4, 6, 8 i 7.siffer i partsnummeret
       // skal IKKE gi utslag dersom selskap er børsnotert
       // skal IKKE gi utslag dersom selskapet "Bolig og eiendom" er utfylt i skattemeldingen/selskapsoppgaven.
       // kontrollen gir merknad i valideringstjenesten    
       naeringsspesifikasjon
     	.forForekomsterAv { balanseregnskap.anleggsmiddel.balanseverdiForAnleggsmiddel }
         .forForekomsterAv { balanseverdi }
         .utslagFor {    

               val harOppgittBalanseverdiForAnleggsmidler = hvorMinstEnForekomst{ (fastsatt(type).finnesBlant("1105", "1115", "1150", "1160", "1180")) }

        		hvis(harOppgittBalanseverdiForAnleggsmidler og 
                    ((partsnrUp.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8")) og 
                     erIkkeBoersnotertUp og
                     harIkkeBoligOgEiendomUP) eller
                     ((partsnrSDF.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8")) og
                     erIkkeBoersnotertSDF og
                     harIkkeBoligOgEiendomSDF)
               ) {
              betjeningsstrategiSettesTil(
              ikke, merknadStandard
                         )
                     }
                 }
         }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)   {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { fastsattEllerGrunnlag(partsnummer) }

        	//kontrollen plukker ut skattepliktige der den skattemessige inntektsvirkningen ved avskriving av driftsmidler ikke samsvarer med innrapporterte opplysninger i temaet spesifikasjon av anleggsmidler
          //AB-test på skattytere som har tallet 0, 2, 4, 6, 8 i 7.siffer i partsnummeret
          //kontrollen gir merknad i valideringstjenesten

          naeringsspesifikasjon
              .utslagFor {
                   val harFullRegnskapsplikt = forForekomsterAv { virksomhet }
                 		.hvorMinstEnForekomst {
                  		regnskapspliktstype erLik "fullRegnskapsplikt"
              			}

                  val erUpSdfEllerEnk = forForekomsterAv { virksomhet }
                  	.hvorMinstEnForekomst{virksomhetstype erLik "enkeltpersonforetak"}

                  val SMVerdiSaldoavskrevetAnleggsmiddel = forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
                      .summer{(vederlagVedRealisasjonOgUttakInntektsfoertIAar.beloep) - (aaretsAvskrivning.beloep) + (aaretsInntektsfoeringAvNegativSaldo.beloep)}

                  val SMVerdiLineærtavskrevetAnleggsmiddel = forForekomsterAv { spesifikasjonAvAnleggsmiddel.lineaertavskrevetAnleggsmiddel }
                      .summer{(vederlagVedRealisasjonOgUttakInntektsfoertIAar.beloep) - (aaretsAvskrivning.beloep)}

                  val SMVerdiGevinstOgTapskonto = forForekomsterAv { spesifikasjonAvAnleggsmiddel.gevinstOgTapskonto }
                      .summer{(inntektFraGevinstOgTapskonto.beloep) - (inntektsfradragFraGevinstOgTapskonto.beloep)}

                  val RMVerdiDriftsinntekt = forForekomsterAv { resultatregnskap.driftsinntekt.annenDriftsinntekt }
                      .forForekomsterAv { inntekt }
                      .filtrer{type erLik "3880"}
                      .summer{beloep.beloep}

                  val RMVerdiDriftskostnad = forForekomsterAv { resultatregnskap.driftskostnad.annenDriftskostnad }
                      .forForekomsterAv { kostnad }
                      .filtrer{type.finnesBlant ("6000","6050","7880")}
                      .summer{beloep.beloep}

                  val VerdiMidlertidigeForskjellDriftsmiddel = forForekomsterAv { midlertidigForskjell }
                      .filtrer { fastsatt(midlertidigForskjellstype) erLik "driftsmiddelOgGoodwill" }
                      .summer {
                          ((regnskapsmessigVerdiForrigeInntektsaar.beloep) - (skattemessigVerdiForrigeInntektsaar.beloep)) -
                              ((regnskapsmessigVerdi.beloep) - (skattemessigVerdi.beloep))
                      }

                  val VerdiMidlertidigeForskjellerPositivGTK = forForekomsterAv { midlertidigForskjell }
                      .filtrer { fastsatt(midlertidigForskjellstype) erLik "positivSaldoPaaGevinstOgTapskonto" }
                      .summer { (skattemessigVerdiForrigeInntektsaar.beloep) - (skattemessigVerdi.beloep) }

                  val VerdiMidlertidigeForskjellerNegativGTK = forForekomsterAv { midlertidigForskjell }
                      .filtrer { fastsatt(midlertidigForskjellstype) erLik "negativSaldoPaaGevinstOgTapskonto" }
                      .summer { (skattemessigVerdiForrigeInntektsaar.beloep) - (skattemessigVerdi.beloep) }

                  val VerdiMidlertidigeForskjellerDifferanseTillegg = forForekomsterAv { midlertidigForskjell }
                      .filtrer { fastsatt(midlertidigForskjellstype) erLik "tilleggForDifferanseMellomBalansefoertOgVirkeligVerdiPaaFusjonsEllerFisjonstidspunktet" }
                      .summer { (regnskapsmessigVerdi.beloep) + (skattemessigVerdi.beloep) }

                  val VerdiMidlertidigeForskjellerDifferanseFradrag = forForekomsterAv { midlertidigForskjell }
                      .filtrer { fastsatt(midlertidigForskjellstype) erLik "fradragForDifferanseMellomBalansefoertOgVirkeligVerdiPaaFusjonsEllerFisjonstidspunktet" }
                      .summer { (regnskapsmessigVerdi.beloep) + (skattemessigVerdi.beloep) }

                  val sumSMVerdi =
                      SMVerdiSaldoavskrevetAnleggsmiddel + SMVerdiLineærtavskrevetAnleggsmiddel + SMVerdiGevinstOgTapskonto

                  val sumRMVerdi = RMVerdiDriftsinntekt - RMVerdiDriftskostnad

                  val sumForskjeller =
                      VerdiMidlertidigeForskjellDriftsmiddel + VerdiMidlertidigeForskjellerPositivGTK - VerdiMidlertidigeForskjellerNegativGTK +
                          VerdiMidlertidigeForskjellerDifferanseTillegg - VerdiMidlertidigeForskjellerDifferanseFradrag

                val avvikRMSM = sumSMVerdi - (sumRMVerdi + sumForskjeller)

                val differanseSMRM = differanse(sumSMVerdi, (sumRMVerdi + sumForskjeller))

                  hvis(harFullRegnskapsplikt og erUpSdfEllerEnk og (differanseSMRM erStørreEnn 1000) og (partsnr.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8",)))
                  {
                      betjeningsstrategiSettesTil(
                          ikke,
                          merknadStandard,
                          1,
                      )
                  }
              }
      }

sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig) {

      val partsnrUp = skattepliktUpersonligGjeldende
        .verdiFor { skattepliktOgOpplysningerOmSkattesubjekt.partsnummer }

      val partsnrSDF = opplysningspliktSdfGjeldende
       .verdiFor{opplysningspliktOgOpplysningerOmSelskapMedDeltakerfastsetting.partsnummer}

      val selskapIkkeIStorbedrift = ansvarligEnhet
      .hvorMinstEnForekomst { erStorbedrift erLik false }

      val rederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemeldingen = skattemeldingUpersonlig
        .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
        .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"} 

       val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
         .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
         .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

      // kontrollen plukker ut skattepliktige der den skattemessige inntektsvirkningen ved avskriving av driftsmidler
      //ikke samsvarer med innrapporterte opplysninger i temaet spesifikasjon av anleggsmidler
      // skal ikke gi utslag dersom skattepliktig er storbedrift eller skattlegges etter særskilt beskatningsordning for rederiskatteordningen
      // AB-test på skattytere som har tallet 0, 2, 4, 6, 8 i 7.siffer i partsnummeret
      // kontrollen gir ikke merknad i valideringstjenesten

      naeringsspesifikasjon
      .utslagFor {
        val harFullRegnskapsplikt = forForekomsterAv { virksomhet }
          .hvorMinstEnForekomst {
            regnskapspliktstype erLik "fullRegnskapsplikt"
          }

        val erUpSdfEllerEnk = forForekomsterAv { virksomhet }
          .hvorMinstEnForekomst{
            virksomhetstype erLik "oevrigSelskap" }

        val SMVerdiSaldoavskrevetAnleggsmiddel = forForekomsterAv { spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel }
          .summer(IkkeInkluderForekomster){(vederlagVedRealisasjonOgUttakInntektsfoertIAar.beloep) - (aaretsAvskrivning.beloep) + (aaretsInntektsfoeringAvNegativSaldo.beloep)}

        val SMVerdiLineærtavskrevetAnleggsmiddel = forForekomsterAv { spesifikasjonAvAnleggsmiddel.lineaertavskrevetAnleggsmiddel }
          .summer(IkkeInkluderForekomster){(vederlagVedRealisasjonOgUttakInntektsfoertIAar.beloep) - (aaretsAvskrivning.beloep)}

        val SMVerdiGevinstOgTapskonto = forForekomsterAv { spesifikasjonAvAnleggsmiddel.gevinstOgTapskonto }
          .summer(IkkeInkluderForekomster){(inntektFraGevinstOgTapskonto.beloep) - (inntektsfradragFraGevinstOgTapskonto.beloep)}

        val SMVerdiSærskiltAnleggsmiddelKraftverk = forForekomsterAv{spesifikasjonAvAnleggsmiddel.saerskiltAnleggsmiddelIKraftverk}
          .summer(IkkeInkluderForekomster){aaretsSamledeAvskrivningForSaerskiltAnleggsmiddelIKraftverk.beloep}

        val RMVerdiDriftsinntekt = forForekomsterAv { resultatregnskap.driftsinntekt.annenDriftsinntekt }
          .forForekomsterAv { inntekt }
          .grupperPå { type }
          .forForekomsterIGruppe { "3880".tekstFelt }
          .summer{beloep.beloep}

        val RMVerdiDriftskostnadGruppert = forForekomsterAv { resultatregnskap.driftskostnad.annenDriftskostnad }
          .forForekomsterAv { kostnad }
          .grupperPå { type }

        val RMVerdiDriftskostnad = (RMVerdiDriftskostnadGruppert.forForekomsterIGruppe { "6000".tekstFelt } +
            RMVerdiDriftskostnadGruppert.forForekomsterIGruppe { "6050".tekstFelt } +
            RMVerdiDriftskostnadGruppert.forForekomsterIGruppe { "7880".tekstFelt })
          .summer(IkkeInkluderForekomster){beloep.beloep}

        val midlertidigForskjellGruppert = forForekomsterAv { midlertidigForskjell }
          .grupperPå { fastsatt(midlertidigForskjellstype) }

        val VerdiMidlertidigeForskjellDriftsmiddel = midlertidigForskjellGruppert
          .forForekomsterIGruppe { "driftsmiddelOgGoodwill".tekstFelt }
          .summer(IkkeInkluderForekomster) {
            ((regnskapsmessigVerdiForrigeInntektsaar.beloep) - (skattemessigVerdiForrigeInntektsaar.beloep)) -
            ((regnskapsmessigVerdi.beloep) - (skattemessigVerdi.beloep))
          }

        val VerdiMidlertidigeForskjellerPositivGTK = midlertidigForskjellGruppert
          .forForekomsterIGruppe { "positivSaldoPaaGevinstOgTapskonto".tekstFelt }
          .summer(IkkeInkluderForekomster) { (skattemessigVerdiForrigeInntektsaar.beloep) - (skattemessigVerdi.beloep) }

        val VerdiMidlertidigeForskjellerNegativGTK = midlertidigForskjellGruppert
          .forForekomsterIGruppe { "negativSaldoPaaGevinstOgTapskonto".tekstFelt }
          .summer(IkkeInkluderForekomster) { (skattemessigVerdiForrigeInntektsaar.beloep) - (skattemessigVerdi.beloep) }

        val VerdiMidlertidigeForskjellerDifferanseTillegg = midlertidigForskjellGruppert
          .forForekomsterIGruppe { "tilleggForDifferanseMellomBalansefoertOgVirkeligVerdiPaaFusjonsEllerFisjonstidspunktet".tekstFelt }
          .summer(IkkeInkluderForekomster) { (regnskapsmessigVerdi.beloep) + (skattemessigVerdi.beloep) }

        val VerdiMidlertidigeForskjellerDifferanseFradrag = midlertidigForskjellGruppert
          .forForekomsterIGruppe { "fradragForDifferanseMellomBalansefoertOgVirkeligVerdiPaaFusjonsEllerFisjonstidspunktet".tekstFelt }
          .summer(IkkeInkluderForekomster) { (regnskapsmessigVerdi.beloep) + (skattemessigVerdi.beloep) }

        val sumSMVerdi =
        SMVerdiSaldoavskrevetAnleggsmiddel + SMVerdiLineærtavskrevetAnleggsmiddel + SMVerdiGevinstOgTapskonto - SMVerdiSærskiltAnleggsmiddelKraftverk

        val sumRMVerdi = RMVerdiDriftsinntekt - RMVerdiDriftskostnad

        val sumForskjeller =
        VerdiMidlertidigeForskjellDriftsmiddel + VerdiMidlertidigeForskjellerPositivGTK - VerdiMidlertidigeForskjellerNegativGTK +
        VerdiMidlertidigeForskjellerDifferanseTillegg - VerdiMidlertidigeForskjellerDifferanseFradrag
        val differanseSMRM = differanse(sumSMVerdi, (sumRMVerdi + sumForskjeller))

        val harMidlertidigForskjellstypeLeasinggjeld = midlertidigForskjellGruppert
          .forForekomsterIGruppe { "regnskapsmessigVerdiAvLeasinggjeld".tekstFelt }
          .hvorMinstEnForekomst{midlertidigForskjellstype erLik "regnskapsmessigVerdiAvLeasinggjeld"}

        val harMidlertidigForskjellstypeLeasingobjekt = midlertidigForskjellGruppert
          .forForekomsterIGruppe { "regnskapsmessigVerdiAvLeasingobjekt".tekstFelt }
          .hvorMinstEnForekomst{midlertidigForskjellstype erLik "regnskapsmessigVerdiAvLeasingobjekt"}

        hvis((ikke(rederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemeldingen) og 
              selskapIkkeIStorbedrift og harFullRegnskapsplikt og erUpSdfEllerEnk og 
              ikke(harMidlertidigForskjellstypeLeasinggjeld) og
              ikke(harMidlertidigForskjellstypeLeasingobjekt) og
              (differanseSMRM erStørreEnn 1_000) og
              (partsnrUp.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8")) eller
             (ikke(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen) og 
              selskapIkkeIStorbedrift og harFullRegnskapsplikt og erUpSdfEllerEnk og 
              ikke(harMidlertidigForskjellstypeLeasinggjeld) og
              ikke(harMidlertidigForskjellstypeLeasingobjekt) og
              (differanseSMRM erStørreEnn 1_000) og
              (partsnrSDF.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8"))))
        )
        {
          betjeningsstrategiSettesTil(
            ikke,
            merknadStandard
          )
        }
      }
    }

sakstype(Upersonlig,SDF)
        .kontekst(ValideringSkattepliktig) {

     	val partsnrUP = skattepliktUpersonligGjeldende
          .verdiFor { skattepliktOgOpplysningerOmSkattesubjekt.partsnummer }

        val partsnrSDF = opplysningspliktSdfGjeldende
          .verdiFor{opplysningspliktOgOpplysningerOmSelskapMedDeltakerfastsetting.partsnummer}

        val selskapIStorbedrift = ansvarligEnhet
          .hvorMinstEnForekomst{erStorbedrift erLik true}

        val rederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
          .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
          .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}   

       val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
          .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
          .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

       val harUtbetalingUpersonlig = skattemeldingUpersonlig
     	  .forForekomsterAv { deltakersAndelAvFormueOgInntekt }
          .summer{fastsatt(utbetalingMvTilDeltaker)}   

        val harUtbetalingSDF = selskapsmeldingSdf
     	  .forForekomsterAv { deltakersAndelAvFormueOgInntekt }
          .summer{fastsatt(utbetalingMvTilDeltaker)} 

    // GJelder for UP og SDF
    // Validering dersom det er rapportert inntekter som skal inntektsføres med 3 %
    // AB-test på skattytere som har tallet 0, 2, 4, 6, 8 i 7.siffer i partsnummeret
    // skal ikke gi utslag dersom skattyter er storbedrift eller skattlegges etter særskilt beskatningsordning for rederiskatteordningen
    // kontrollen gir merknadStandard i valideringstjenesten
    naeringsspesifikasjon
          .utslagFor{  
    		val harFullRegnskapsplikt = forForekomsterAv { virksomhet }
            	.hvorMinstEnForekomst{regnskapspliktstype erLik "fullRegnskapsplikt"}
            val harGyldigVirksomhetstype = forForekomsterAv { virksomhet }
            	.hvorMinstEnForekomst{virksomhetstype erLik "oevrigSelskap"}
            val harIkkePermanentForskjell = harIngenForekomsterDer{fastsatt(permanentForskjell) er oppgitt}
         	val harIkkeInntektsført3Prosent = forForekomsterAv { permanentForskjell }
          		.harIngenForekomsterDer{fastsatt(permanentForskjellstype) erLik "skattepliktigDelAvUtbytterOgUtdelinger"}
          	val harTilbakeførtSomPermanentForskjell = forForekomsterAv { permanentForskjell }
          		.filtrer{fastsatt(permanentForskjellstype) erLik "tilbakefoeringAvInntektsfoertUtbytte"}
                .summer{beloep.beloep}

            	hvis(ikke(selskapIStorbedrift) og
                     ((ikke(rederibeskatningsordningBehandlesEtterSaerskilteRegler) og
                     harFullRegnskapsplikt og 
                     harGyldigVirksomhetstype og 
                      (verdiFor{ naeringsspesifikasjon.skalBekreftesAvRevisor } erLik "false") og 
                     (((harUtbetalingUpersonlig erStørreEnn 100_000) og (harIkkePermanentForskjell eller harIkkeInntektsført3Prosent)) eller
                      ((harTilbakeførtSomPermanentForskjell erStørreEnn 100_000) og (harIkkePermanentForskjell eller harIkkeInntektsført3Prosent))) og
                      (partsnrUP.tegnFraIndeks(7).finnesBlant("0","2","4","6","8"))) eller
                    (ikke(harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen) og
