sakstype(Personlig, Upersonlig, SDF)
    .kontekst(ValideringSkattepliktig) 
     .hvis({
                val storbedriftEllerRederibeskatningsordningBehandlesEtterSaerskilteReglerUP = hvis((sakstype er Upersonlig)){

                  val erRederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
                      .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
                      .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

                 val selskapUPIStorbedrift = ansvarligEnhet
                   .hvorMinstEnForekomst{erStorbedrift erLik true}      

                   erRederibeskatningsordningBehandlesEtterSaerskilteRegler eller selskapUPIStorbedrift
                }     

                val selskapSDFIStorbedrift = hvis((sakstype er SDF)){
                  val selskapIStorbedrift = ansvarligEnhet
                      .hvorMinstEnForekomst{erStorbedrift erLik true}

                   selskapIStorbedrift
                }
               ikke(storbedriftEllerRederibeskatningsordningBehandlesEtterSaerskilteReglerUP) og ikke(selskapSDFIStorbedrift)
            })
      {

       val harIkkeGevinstOgTapskonto = naeringsspesifikasjon
          .forForekomsterAv { spesifikasjonAvAnleggsmiddel.gevinstOgTapskonto }
          .harIngenForekomsterDer { fastsatt(kommunenummer) er oppgitt }

        // kontrollen gir utslag dersom skattyter har angitt beløp større enn 0 i "Gevinst overført til gevinst- og tapskonto" 
        //eller "Tap overført til gevinst og tapskonto", 
        // men gevinst- og tapskonto er ikke rapportert
        // skal IKKE kjøre og gi utslag på rederi (for UP) 
        // og ikke kjøre og gi utslag for storbedrift (for UP og SDF)
        naeringsspesifikasjon
      	.forForekomsterAv {
            grupper(
                spesifikasjonAvAnleggsmiddel.saldoavskrevetAnleggsmiddel,
                spesifikasjonAvAnleggsmiddel.lineaertavskrevetAnleggsmiddel,
                spesifikasjonAvAnleggsmiddel.ikkeAvskrivbartAnleggsmiddel,
            )
        }
            .utslagFor {

               val harOppgittGevinstEllerTapOverfoertTilGTK = hvorMinstEnForekomst {
                    ((fastsatt(gevinstOverfoertTilGevinstOgTapskonto.beloep) er oppgitt) og 
                     (fastsatt(saldogruppe).finnesBlant("b", "e", "f", "g", "h", "i")) og 
                      harIkkeGevinstOgTapskonto) eller
                    ((fastsatt(tapOverfoertTilGevinstOgTapskonto.beloep) er oppgitt) og 
                     (fastsatt(saldogruppe).finnesBlant("e", "f", "g", "h", "i")) og 
                     harIkkeGevinstOgTapskonto)
                }

                hvis(harOppgittGevinstEllerTapOverfoertTilGTK)
                {
                    betjeningsstrategiSettesTil(
                        ikke, 
                        merknadTips

                    )
                }
            }
    }

sakstype(Upersonlig, SDF)
    .kontekst(ValideringFoerBeregning) {
        // kontrollen gir utslag dersom skattepliktig med virksomhetstype oevrigSelskap rapporterer samletGjeldOgFormuesobjekt
        // kontrollen gir faktiskFeil i valideringstjenesten

        val harVirksomhetstypeLikAksjeselskap = naeringsspesifikasjon
            .forForekomsterAv { virksomhet }
            .hvorMinstEnForekomst {virksomhetstype.finnesBlant(
            "oevrigSelskap",
            "bankOgFinansieringsforetak",
            "selskapMedDeltakerfastsetting",
            "livsforsikringsforetakOgPensjonskasse",
            "skadeforsikringsforetak",
            "samvirkeforetak",
            "nokus")
            }

        val rapportererGjeldOgFormuesobjekter = naeringsspesifikasjon
            .forForekomsterAv { samletGjeldOgFormuesobjekter }
            .hvorMinstEnForekomst { forekomst ->
                (
                    (formuesverdiForFormuesobjekterIkkeOmfattetAvVerdsettingsrabatt.beloep er oppgitt) eller
                        (verdiFoerVerdsettingsrabattForFormuesobjekterOmfattetAvVerdsettingsrabatt.beloep er oppgitt) eller
                        (samletGjeld.beloep er oppgitt) eller forekomst
                            .forForekomsterAv {samletGjeldOgFormuesobjekterTilordnetInnehaverPerKommune}
                            .hvorMinstEnForekomst {
                        (formuesverdiForFormuesobjekterIkkeOmfattetAvVerdsettingsrabattTilordnetInnehaver.beloep er oppgitt) eller
                            (verdiFoerVerdsettingsrabattForFormuesobjekterOmfattetAvVerdsettingsrabattTilordnetInnehaver.beloep er oppgitt) eller
                            (samletGjeldTilordnetInnehaver.beloep er oppgitt)
                    }
                    )
            }

        naeringsspesifikasjon
            .utslagFor {

                hvis(harVirksomhetstypeLikAksjeselskap og rapportererGjeldOgFormuesobjekter) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil,
                        1,
                        Gruppering settesTil Næring
                    )
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

        // kontrollen gir utslag dersom UP har krevd fradrag for betalt skatt i utlandet (samletInntektsskattBetaltIUtlandet > 0)
        // men ikke har fylt ut inntekter og kostnader fra utlandet i feltene 'Kreditfradrag av annen utenlandsinntekt' under tema 'Metode for dobbeltbeskatning' i skattemeldingen.
        //    (samletUtenlandsinntekt + samletKostnadTilordnetUtlandetEtterIndirekteFordelingsmetode + samletKostnadTilordnetUtlandetEtterIndirekteEllerAnnenFordelingsmetode = 0)
        // kontrollen gir merknad i valideringstjenesten            
        skattemeldingUpersonlig
            .utslagFor {

                val sumFradragForbetaltInntektsskattTilUtlandet = forForekomsterAv {
                   grupper(fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilAnnenUtenlandsinntekt,
                           fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilInntektFraNOKUSSelskapEllerVirksomhetILavskatteland,
                           fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilInntektFraNOKUSSelskapEllerVirksomhetILavskattelandForVirksomhetPaaSokkel,
                           fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilAnnenUtenlandsinntektForVirksomhetPaaSokkel)}
                   .summer { samletInntektsskattBetaltIUtlandet }

                val sumInntekterOgKostnaderIUtlandet = forForekomsterAv {
                   grupper(fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilAnnenUtenlandsinntekt,
                           fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilInntektFraNOKUSSelskapEllerVirksomhetILavskatteland,
                           fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilInntektFraNOKUSSelskapEllerVirksomhetILavskattelandForVirksomhetPaaSokkel,
                           fradragINorskInntektsskattForSkattBetaltTilFremmedStatKnyttetTilAnnenUtenlandsinntektForVirksomhetPaaSokkel)}
                   .summer { samletUtenlandsinntekt + samletKostnadTilordnetUtlandetEtterIndirekteFordelingsmetode + samletKostnadTilordnetUtlandetEtterIndirekteEllerAnnenFordelingsmetode}      

                hvis((sumFradragForbetaltInntektsskattTilUtlandet erStørreEnn 0) og
                     (sumInntekterOgKostnaderIUtlandet erLik 0))
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
    }

