    .kontekst(ValideringSkattepliktig)
    {
      val harSkattepliktHavbruk =  skattepliktUpersonligGjeldende
         .forForekomsterAv {skatteplikt}
         .hvorMinstEnForekomst{ erOmfattetAvSaerreglerForHavbruksvirksomhet erLik true}

      val harOpplysningspliktHavbruk =  opplysningspliktSdfGjeldende
        .forForekomsterAv {opplysningsplikt}
        .hvorMinstEnForekomst{ erOmfattetAvSaerreglerForHavbruksvirksomhet erLik true} 

       val sumPositivGrunnrenteinntektFoerSamordningUP = skattemeldingUpersonlig
         .forForekomsterAv{havbruksvirksomhet}
         .filtrer{beregnetGrunnrenteskatt.positivGrunnrenteinntektFoerSamordning erStørreEnn 0}
         .summer{beregnetGrunnrenteskatt.positivGrunnrenteinntektFoerSamordning}

       val sumNegativGrunnrenteinntektFoerSamordningUP = skattemeldingUpersonlig
         .forForekomsterAv{havbruksvirksomhet}
         .filtrer{beregnetGrunnrenteskatt.negativGrunnrenteinntektFoerSamordning erStørreEnn 0}
         .summer{beregnetGrunnrenteskatt.negativGrunnrenteinntektFoerSamordning}

       val sumPositivGrunnrenteinntektFoerSamordningSDF = selskapsmeldingSdf 
          .forForekomsterAv{havbruksvirksomhet}
          .filtrer{beregnetGrunnrenteskatt.positivGrunnrenteinntektFoerSamordning erStørreEnn 0}
          .summer{beregnetGrunnrenteskatt.positivGrunnrenteinntektFoerSamordning}

       val sumNegativGrunnrenteinntektFoerSamordningSDF = selskapsmeldingSdf 
          .forForekomsterAv{havbruksvirksomhet}
          .filtrer{beregnetGrunnrenteskatt.negativGrunnrenteinntektFoerSamordning erStørreEnn 0}
          .summer{beregnetGrunnrenteskatt.negativGrunnrenteinntektFoerSamordning}

        // Gir utslag hvis innsending gjelder upersonlig skattepliktig og SDF
        // OG skatteplikten/opplysningsplikten er omfattet av særregler for havbruk
        // OG Selskapet har oppgitt positivGrunnrenteinntektFoerSamordning i skattemelding større enn 0 og den er ulik grunnrenteinntektFoerSamordning i NS
        //     ELLER har oppgitt negativGrunnrenteinntektFoerSamordning i skattemelding større enn 0 og den er ulik grunnrenteinntektFoerSamordning * -1 i NS
        // når avviket er større enn 100
       naeringsspesifikasjon
        .utslagFor {

       val sumGrunnrenteinntektFoerSamordning = forForekomsterAv{spesifikasjonAvGrunnrenteinntektIHavbruksvirksomhet}
         .summer{grunnrenteinntektFoerSamordning.beloep}

       val negativsumGrunnrenteinntektFoerSamordning = sumGrunnrenteinntektFoerSamordning * -1  

       val differansePositivGrunnrenteinntektErUlikSumGrunnrenteinntektUP = 
          differanse (sumPositivGrunnrenteinntektFoerSamordningUP, sumGrunnrenteinntektFoerSamordning)

       val differanseNegativGrunnrenteinntektErUlikNegativsumGrunnrenteinntektUP = 
          differanse (sumNegativGrunnrenteinntektFoerSamordningUP, negativsumGrunnrenteinntektFoerSamordning)

       val differansePositivGrunnrenteinntektErUlikSumGrunnrenteinntektSDF = 
          differanse (sumPositivGrunnrenteinntektFoerSamordningSDF, sumGrunnrenteinntektFoerSamordning)

       val differanseNegativGrunnrenteinntektErUlikNegativsumGrunnrenteinntektSDF = 
          differanse (sumNegativGrunnrenteinntektFoerSamordningSDF, negativsumGrunnrenteinntektFoerSamordning)

      hvis((harSkattepliktHavbruk og 
           (((sumPositivGrunnrenteinntektFoerSamordningUP erStørreEnn 0) og (differansePositivGrunnrenteinntektErUlikSumGrunnrenteinntektUP erStørreEnn 100)) eller
            ((sumNegativGrunnrenteinntektFoerSamordningUP erStørreEnn 0) og (differanseNegativGrunnrenteinntektErUlikNegativsumGrunnrenteinntektUP erStørreEnn 100)))) eller
           (harOpplysningspliktHavbruk og
           (((sumPositivGrunnrenteinntektFoerSamordningSDF erStørreEnn 0) og (differansePositivGrunnrenteinntektErUlikSumGrunnrenteinntektSDF erStørreEnn 100)) eller
            ((sumNegativGrunnrenteinntektFoerSamordningSDF erStørreEnn 0) og (differanseNegativGrunnrenteinntektErUlikNegativsumGrunnrenteinntektSDF erStørreEnn 100))))
              )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)                    
               }
            }
       }

