sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) {
        //kontrollen gir utslag dersom innrapportert næringsinntekt er større enn sum driftsinntekt i resultatregnskapet
        // AB-test på skattytere som har tallet 0, 2, 4, 6, 8 i 7.siffer i partsnummeret
     //kontrollen gir merknad

      	val partsnr = skattepliktGjeldende
            	.forForekomsterAv { skatteplikt }
            	.verdiFor { fastsattEllerGrunnlag(partsnummer) }

          val driftsinntekterSummert = naeringsspesifikasjon
             	.summer { resultatregnskap.driftsinntekt.sumDriftsinntekt.beloep }

          val driftsinntekterSomUnntas = naeringsspesifikasjon
      		.forForekomsterAv { resultatregnskap.driftsinntekt.annenDriftsinntekt }
             	.forForekomsterAv { inntekt }
             	.filtrer { (type).finnesBlant("3890", "3895", "3910") }
             	.summer { beloep.beloep }

         	val sumInnrapportertNæringsinntekt = utgåtteSkattemeldingFelter {
                skattemeldingUtgått
                .forForekomsterAv(oppgavegiver)
                .summerIkkeInkluderForekomster { inntektFraUtbetalingTilSelvstendigNaeringsdrivendeOverGrenseverdi }
            }

          val avvikBeløp = sumInnrapportertNæringsinntekt - driftsinntekterSummert + driftsinntekterSomUnntas      

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(naeringsinntektMv)
            .utslagFor {

                hvis(
                    (sumInnrapportertNæringsinntekt erStørreEnn 0) og (naeringstype er oppgitt) og (avvikBeløp erStørreEnnEllerLik 1000) og
                        (partsnr.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8"))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        Avviksbeloep settesTil avvikBeløp
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) 
    .hvis({ naeringsspesifikasjon.harInnhold }) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor {fastsattEllerGrunnlag(partsnummer) }

        //kontrollen gir utslag dersom skattepliktig skattepliktig står på kontrolliste over tømmeromsetning og ikke rapporterer salgsinntekt i temaet Skog og tømmerkonto
        //kontrollen gir validering i SME

      	val erLeveringspliktig = opplysningerOmSkattesubjektGjeldendeDokument
            .forForekomsterAv{opplysningerOmSkattesubjekt}
            .hvorMinstEnForekomst{leveringsplikt.harLeveringsplikt erLik true}

      	val tilvekstSkogOver200m3 = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.forForekomsterAv(formuesgrunnlagSkogeiendomINorge)
            .summer { fastsatt(sumTilvekst.beloepSomTall) }
        }

        val beløpKapitalinntektSkog = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.forForekomsterAv(kapitalkostnadOgKapitalinntektFraSkogeiendomUtenforNaeringsvirksomhet)
            .summer { kapitalinntekt.beloepSomTall }
        }

       /* val harIkkeKapitalinntektSkog = skattemelding
      		.forForekomsterAv(kapitalkostnadOgKapitalinntektFraSkogeiendomUtenforNaeringsvirksomhet)
      		.harIngenForekomsterDer{kapitalinntekt.beloepSomTall er oppgitt} */

        naeringsspesifikasjon
            .utslagFor {

              val beløpIKontrollisteTømmer = hentKontrolliste(
                    "kontrolliste - toemmeromsetning",
                    listeAv(partsnr)
                ).forForekomsterAv { verdi }
                    .summer { verdiSomTall1 }

               val harIkkeTømmerkonto = harIngenForekomsterDer { skogbruk.skogOgToemmerkonto er oppgitt}

               val harIkkeSalgsinntekt = harIngenForekomsterDer{resultatregnskap.driftsinntekt.salgsinntekt er oppgitt}

               val differanseKapitalinntektOgKontrollistebeløp = beløpKapitalinntektSkog - beløpIKontrollisteTømmer

                hvis((((beløpIKontrollisteTømmer erStørreEnn 0) og (tilvekstSkogOver200m3 erStørreEnn 200)) eller ((beløpIKontrollisteTømmer erStørreEnn 0) og erLeveringspliktig)) og 
                     harIkkeTømmerkonto og harIkkeSalgsinntekt og (differanseKapitalinntektOgKontrollistebeløp erStørreEnn 100000)
                    )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadTips
                    )
                }
            }
    }

