                val harFastsattAnnetFinansproduktUtenErOmfattetAvFritaksmetoden = forForekomsterAv{spesifikasjonAvForholdRelevanteForBeskatning.annetFinansprodukt}
                   .hvorMinstEnForekomst{
                     ((fastsatt(finansproduktnavn) er oppgitt) eller (fastsatt(kontonummer) er oppgitt)) og
                     (fastsatt(erOmfattetAvFritaksmetoden) ikkeEr oppgitt)
                   }   

                val harFastsattVerdipapirfondUtenErOmfattetAvFritaksmetoden = forForekomsterAv{spesifikasjonAvForholdRelevanteForBeskatning.verdipapirfond}
                   .hvorMinstEnForekomst{
                     ((fastsatt(fondetsNavn) er oppgitt) eller (fastsatt(fondetsOrganisasjonsnummer) er oppgitt)) og
                     (fastsatt(erOmfattetAvFritaksmetoden) ikkeEr oppgitt)
                   }     

                hvis(harFastsattAksjeIAksjonaerregisteretUtenErOmfattetAvFritaksmetoden eller
                    harFastsattAksjeIkkeIAksjonaerregisteretUtenErOmfattetAvFritaksmetoden eller
                    harFastsattAnnetFinansproduktUtenErOmfattetAvFritaksmetoden eller
                    harFastsattVerdipapirfondUtenErOmfattetAvFritaksmetoden
                    ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning)
     {
        // kontrollen gir utslag dersom skattyters er næringsdrivende (harLeveringsplikt = true i OOSS)
        // og ikke har oppgitt næringsspesifikasjon 
        //       eller opplysninger om deltakers andel i SDF 
        //       eller næringsinntekt fordelt fra ektefelle
        // kontrollen gir merknadMangel i SME

        val erLeveringspliktig = opplysningerOmSkattesubjektGjeldendeDokument
            .forForekomsterAv{opplysningerOmSkattesubjekt}
            .hvorMinstEnForekomst{leveringsplikt.harLeveringsplikt erLik true}

        val harNaeringsspesifikasjon = naeringsspesifikasjon.harInnhold

