sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig) {

      val partsnrUP = skattepliktUpersonligGjeldende
      .verdiFor{skattepliktOgOpplysningerOmSkattesubjekt.partsnummer}

      val partsnrSDF = opplysningspliktSdfGjeldende
        .verdiFor{opplysningspliktOgOpplysningerOmSelskapMedDeltakerfastsetting.partsnummer}

      val harYtelserMellomAksjonaerOgSelskapISMUUP = skattemeldingUpersonlig
           .forForekomsterAv{opplysningOmSkattesubjekt}
           .hvorMinstEnForekomst { harYtelseMellomAksjonaerEllerNaerstaaendeOgSelskapEllerSelskapetsDatterselskap erLik true }

       val harYtelserMellomAksjonaerOgSelskapISMUSDF = selskapsmeldingSdf
           .forForekomsterAv{opplysningOmSelskapMedDeltakerfastsetting}
           .hvorMinstEnForekomst { harYtelseMellomAksjonaerEllerNaerstaaendeOgSelskapEllerSelskapetsDatterselskap erLik true }

      // Skal gi utslag UP og SDF 
      // AB-test på skattytere som har partall i 7.siffer i partsnummeret
       naeringsspesifikasjon
         .utslagFor {

         val harYtelseFraAksjonaerEllerNaerstaaendeINS = forForekomsterAv { andreForhold.ytelseFraAksjonaerDeltakerEllerNaerstaaende }
            .hvorMinstEnForekomst {
                (laan.kostnadsfoerteRenter erStørreEnn 0) eller
                    (laan.laanesaldoVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (laan.laanesaldoPer061015 erStørreEnn 0) eller
                    (sikkerhetsstillelse.beloepPer061015 erStørreEnn 0) eller
                    (sikkerhetsstillelse.beloepVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (realisasjonOgUtleieAvFormuesobjektMv er oppgitt) }

        val harYtelseFraSelskapINS = forForekomsterAv { andreForhold.ytelseFraSelskapEllerSelskapetsDatterselskap }
            .hvorMinstEnForekomst {
                (totaltUtdeltUtbytteVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (totaltUtdeltUtbytteVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (laan.laanesaldoVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (laan.laanesaldoPer061015 erStørreEnn 0) eller
                    (sikkerhetsstillelse.beloepPer061015 erStørreEnn 0) eller
                    (sikkerhetsstillelse.beloepVedUtgangAvInntektsaar erStørreEnn 0) eller
                    (realisasjonOgUtleieAvFormuesobjektMv er oppgitt) }

          hvis((harYtelserMellomAksjonaerOgSelskapISMUUP og 
                (ikke(harYtelseFraAksjonaerEllerNaerstaaendeINS eller harYtelseFraSelskapINS)) og
                (partsnrUP.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8"))) eller
               (harYtelserMellomAksjonaerOgSelskapISMUSDF og 
                (ikke(harYtelseFraAksjonaerEllerNaerstaaendeINS eller harYtelseFraSelskapINS)) og
                (partsnrSDF.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8"))) )
              {
                 betjeningsstrategiSettesTil(
                   ikke, 
                    merknadStandard)
                }
            }
    }

