        //kontrollen gir utslag dersom skattyter finnes i kontrolliste (innrapportert i mva) og ikke har rapportert drifsinntekter eller driftskostnader i NSP
        // AB-test på skattytere som har partall i 7.siffer i partsnummeret
        //kontrollen gir merknad

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }

        val harIkkeDriftsinntekter = naeringsspesifikasjon
            .summer { resultatregnskap.driftsinntekt.sumDriftsinntekt.beloep }

        val harIkkeDriftskostnader = naeringsspesifikasjon
            .summer { resultatregnskap.driftskostnad.sumDriftskostnad.beloep }

       skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{naeringsinntektMv}
            .utslagFor {

                val finnesIKontrollisteMva = finnesIKontrolliste(

                    "Innrapportert i mva",
                    listeAv(partsnr)
                )

                hvis(
                    finnesIKontrollisteMva og (harIkkeDriftsinntekter erLik 0) og (harIkkeDriftskostnader erLik 0) og
                        (partsnr.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8"))
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadInformasjon
                    )
                }
            }
        }

