sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

      val harErOmfattetAvPetroleumsskatteloven = skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik true}

        // Gir utslag på upersonlig skattyter som i skatteplikt er omfattet av petroleumsskatteloven
        // og hvor summen av gitte balanseverdier for anleggsmidler er ulik regnskapsmessig verdi av midlertidige forskjeller Alminnelig inntekt (sokkel og land)
        //og MF knyttet til fjerningseiendel og RM verdi av leasingobjekt i balansen (mer ennn 10 000)
       naeringsspesifikasjon
         .utslagFor {    

           val sumBalanseverdiForAnleggsmidler = forForekomsterAv { balanseregnskap.anleggsmiddel.balanseverdiForAnleggsmiddel }
              .forForekomsterAv { balanseverdi }
              .filtrer{fastsatt(type).finnesBlant("1000", "1020", "1080", "1101", "1102", "1103", "1104", "1105", 
                                                  "1115", "1117", "1120", "1130", "1140", "1150", "1160","1180", 
                                                  "1205", "1221", "1225", "1238", "1239", "1280", "1290")}
              .summer{fastsatt(beloep.beloep)}

           val sumRegnskapsmessigVerdiMidlertidigForskjell = forForekomsterAv{midlertidigForskjellForVirksomhetOmfattetAvPetroleumsskatteloven}
              .summer{fastsatt(midlertidigForskjellKnyttetTilAlminneligInntektFraVirksomhetPaaSokkel.regnskapsmessigVerdi.beloep) + 
                      fastsatt(midlertidigForskjellKnyttetTilAlminneligInntektFraVirksomhetPaaLand.regnskapsmessigVerdi.beloep)}

           val differanseBalanseverdiOgRegnskapsmessigverdi = differanse(sumBalanseverdiForAnleggsmidler, sumRegnskapsmessigVerdiMidlertidigForskjell)              

    		hvis(harErOmfattetAvPetroleumsskatteloven og 
                 (differanseBalanseverdiOgRegnskapsmessigverdi erStørreEnn 10_000))
              {
                  betjeningsstrategiSettesTil(
                        ikke, merknadStandard)
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

      val harErOmfattetAvPetroleumsskatteloven = skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik true}

        // Gir utslag på upersonlig skattyter som i skatteplikt er omfattet av petroleumsskatteloven
        // og 3001 Salgsinntekt og uttak av olje er ulik OljesalgOgNormprisinntekt.samletBokfoertSalgPerFelt (mer ennn 10 000)
        naeringsspesifikasjon
            .utslagFor {

              val sumSalgsinntektOgUttakAvOlje = forForekomsterAv{resultatregnskap.driftsinntekt.salgsinntekt}
                 .forForekomsterAv{inntekt}
                 .filtrer{fastsatt(type) erLik "3001"}
                 .summer{beloepForVirksomhetOmfattetAvPetroleumsskatteloven.beloep.beloep}

              val sumnOljesalgOgNormprisinntektSamletBokfoertSalgPerFelt = forForekomsterAv{oljesalgOgNormprisinntekt}
                 .summer{samletBokfoertSalgPerFelt.beloep}

              val diffSalgsinntektOgUttakAvOljeOgSamletBokfoertSalg = differanse(sumSalgsinntektOgUttakAvOlje, sumnOljesalgOgNormprisinntektSamletBokfoertSalgPerFelt)

               hvis(harErOmfattetAvPetroleumsskatteloven og
                   (diffSalgsinntektOgUttakAvOljeOgSamletBokfoertSalg erStørreEnn 10_000)) 
              {
                  betjeningsstrategiSettesTil(
                        ikke, merknadStandard)
                }
            }
    }

sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

      val harErOmfattetAvPetroleumsskatteloven = skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik true}

        // Gir utslag på upersonlig skattyter som i skatteplikt er omfattet av petroleumsskatteloven
        // Næringsspesifikasjon/Resulatregnskap/Driftskostnader/Andre driftskostnader/kodelistevalg 7651 Tilskudd til vitenskapelig forskning mv jf. sktl. § 6-42 > 0
        // og er ulik
        //Næringsspesifikasjon/ForskjellMellomRegnskapsmessigOgSkattemessigVerdi/PermanentForskjellForVirksomhetOmfattetAvPetroelusmsskatteloven/
        //Særskattegrunnlag på sokkel/Permanent forskjellstype: Tilbakeføring av fradrag for tilskudd til vitenskapelig forskning og yrkesopplæring
        // Skal ikke gi utslag under 1000
        naeringsspesifikasjon
            .utslagFor {

            val harBeloepTilskuddTilVitenskapeligForskning = forForekomsterAv{resultatregnskap.driftskostnad.annenDriftskostnad}
              .forForekomsterAv{kostnad}
              .filtrer{fastsatt(type) erLik "7651"}
              .hvorMinstEnForekomst{beloepForVirksomhetOmfattetAvPetroleumsskatteloven.beloep.beloep erStørreEnn 0}

          val harTilskuddTilVitenskapeligForskning = forForekomsterAv{resultatregnskap.driftskostnad.annenDriftskostnad}
              .forForekomsterAv{kostnad}
              .filtrer{fastsatt(type) erLik "7651"}
              .summer{beloepForVirksomhetOmfattetAvPetroleumsskatteloven.beloep.beloep}

           val harTilbakefoeringAvFradrag = 
               forForekomsterAv{permanentForskjellForVirksomhetOmfattetAvPetroleumsskatteloven}
              .filtrer{fastsatt(permanentForskjellstype) erLik "tilbakefoeringAvFradragForTilskuddTilVitenskapeligForskningOgYrkesopplaering"}  
              .summer{beloep.beloepSaerskattegrunnlagFraVirksomhetPaaSokkel.beloep}

           val tilskuddTilVitenskapeligForskningUlikTilbakefoeringAvFradrag = differanse (harTilskuddTilVitenskapeligForskning, harTilbakefoeringAvFradrag)

           hvis(harErOmfattetAvPetroleumsskatteloven og harBeloepTilskuddTilVitenskapeligForskning og
                (tilskuddTilVitenskapeligForskningUlikTilbakefoeringAvFradrag erStørreEnn 1000)
