    .kontekst(ValideringFoerBeregning) {
        // kontrollen gir utslag dersom skattepliktig som ikke er enkeltpersonforetak rapporterer personinntekt
        // kontrollen gir faktiskFeil i valideringstjenesten

         val harGyldigVirksomhetstype = naeringsspesifikasjon
      		.forForekomsterAv { virksomhet }
            .hvorMinstEnForekomst{virksomhetstype.finnesBlant(
             "oevrigSelskap",
             "selskapMedDeltakerfastsetting",
             "bankOgFinansieringsforetak", 
             "livsforsikringsforetakOgPensjonskasse",
             "skadeforsikringsforetak",
             "samvirkeforetak",
             "nokus") 
            }

        val harRapportertPersoninntekt = naeringsspesifikasjon
            .forForekomsterAv { fordeltBeregnetPersoninntekt }
            .hvorMinstEnForekomst {
                (identifikatorForFordeltBeregnetPersoninntekt er oppgitt)
            }

        naeringsspesifikasjon
            .utslagFor {

                hvis(harGyldigVirksomhetstype og harRapportertPersoninntekt) {
