sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        //kontrollen gir utslag dersom skattyter har valgt 'Ikke skattepliktig eller trygdepliktig' som trygde- og skattemessig unntak for næringsinntekt,
        //og det mangler trygde- og skattemessig unntak 'Ikke skattepliktig eller trygdepliktig' for personinntekten.
        //kontrollen gir merknad i SME

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(naeringsinntektMv)
            .utslagFor {

                val harSkattefritakPåNæringsinntekt = hvorMinstEnForekomst {
                    (
                        (naeringsinntektTilordnetInnehaver.beloepSomTall er oppgitt) og
                            (
                                fastsatt(naeringsinntektTilordnetInnehaver.trygdeEllerSkattemessigUnntak) erLik (
                                    "skattefriUtenTrygdeavgiftsplikt"
                                    )
                                )
                        ) eller
                        (
                            (underskuddTilordnetInnehaver.beloepSomTall er oppgitt) og
                                (
                                    fastsatt(underskuddTilordnetInnehaver.trygdeEllerSkattemessigUnntak) erLik (
                                        "skattefriUtenTrygdeavgiftsplikt"
                                        )
                                    )
                            )
                }

                val harIkkeTrygdefritakPåPersoninntekt = hvorMinstEnForekomst {
                    (samordnetPersoninntekt.personinntekt.beloepSomTall er oppgitt) og
                        (
                            fastsatt(samordnetPersoninntekt.personinntekt.trygdeEllerSkattemessigUnntak) erUlik (
                                "skattefriUtenTrygdeavgiftsplikt"
                                )
                            )
                }

                hvis(harSkattefritakPåNæringsinntekt og harIkkeTrygdefritakPåPersoninntekt) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

