sakstype(Upersonlig,SDF)
    .kontekst(ValideringSkattepliktig)
      {
        val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding = skattemeldingUpersonlig
             .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
             .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

       val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
            .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
            .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

       val harIkkeSamletTonnasjekattISdf = selskapsmeldingSdf
           .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .hvorMinstEnForekomst{samletTonnasjeskattIEgetSelskap erLik 0}    

       val harIkkeSamletTonnasjeskattIUp = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .hvorMinstEnForekomst{samletTonnasjeskattIEgetSelskap erLik 0}

       val harOppgittTonnasjeskattUP = skattemeldingUpersonlig
         .hvorMinstEnForekomst{rederiskatteordning.tonnasjeskatt er oppgitt}

        val harOppgittTonnasjeskattSDF = selskapsmeldingSdf
          .hvorMinstEnForekomst{rederiskatteordning.tonnasjeskatt er oppgitt}

       val harIkkeNybyggingskonraktSkipISdf = selskapsmeldingSdf
          .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .hvorMinstEnForekomst{(eierSelskapetNybyggingskontraktPaaSkip erLik false) eller
                                  (eierSelskapetNybyggingskontraktPaaSkip ikkeEr oppgitt)}

      val harIkkeNybyggingskonraktSkipIUp = skattemeldingUpersonlig
          .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .hvorMinstEnForekomst{(eierSelskapetNybyggingskontraktPaaSkip erLik false) eller
                                  (eierSelskapetNybyggingskontraktPaaSkip ikkeEr oppgitt)}

        // Gir utslag hvis innsending gjelder Upersonlig og SDF
        // Selskapet/deltaker er innenfor rederiskatteordningen og
        // Næringsspesifikasjon: Balanseregnskap-Anleggsmidler-  1221 Skip, rigger mv er ulik null og
        // (Rederiskatteordning.Tonnasjeskatt.samletTonnasjeskattEgetSelskap er blank/null eller
        //tema Rederiskatteordning.Tonnasjeskatt mangler helt) og
        // Rederiskatteordning.Tonnasjeskatt.eierSelskapetNybyggingskontraktPåSkip er blank eller svart Nei
      naeringsspesifikasjon
        .utslagFor {

      val harBeløpPåKonto1221 = 
     	 forForekomsterAv{balanseregnskap.anleggsmiddel.balanseverdiForAnleggsmiddel}
       	.forForekomsterAv{balanseverdi}
        .filtrer{fastsatt(type) erLik "1221"}
        .hvorMinstEnForekomst{(beloep.beloep) er oppgitt}

        hvis(harBeløpPåKonto1221 og
             ((harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding  og
              (ikke(harOppgittTonnasjeskattUP) eller 
               (harOppgittTonnasjeskattUP og harIkkeSamletTonnasjeskattIUp)) og
              (ikke(harOppgittTonnasjeskattUP) eller 
               (harOppgittTonnasjeskattUP og harIkkeNybyggingskonraktSkipIUp))) eller
              (harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen og
               (ikke(harOppgittTonnasjeskattSDF) eller
                (harOppgittTonnasjeskattSDF og harIkkeSamletTonnasjekattISdf)) og
                (ikke(harOppgittTonnasjeskattSDF) eller
                (harOppgittTonnasjeskattSDF og harIkkeNybyggingskonraktSkipISdf))))
               )
               {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)                    
               }
            }
       }

