    .kontekst(ValideringSkattepliktig)
      {
        val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding = skattemeldingUpersonlig
             .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
             .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

       val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
            .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
            .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

       val harGevinstFinansiellValutakontraktISdf = selskapsmeldingSdf
           .forForekomsterAv{rederiskatteordning.finansinntektOgFinansunderskudd}
           .hvorMinstEnForekomst{skattepliktigGevinstVedRealisasjonAvFinansiellValutakontraktInngaattFom2010 erStørreEnn 0}    

       val harGevinstFinansiellValutakontraktIUp = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.finansinntektOgFinansunderskudd}
           .hvorMinstEnForekomst{skattepliktigGevinstVedRealisasjonAvFinansiellValutakontraktInngaattFom2010 erStørreEnn 0}

       val harTapFinansiellValutakontraktISdf = selskapsmeldingSdf
          .forForekomsterAv{rederiskatteordning.finansinntektOgFinansunderskudd}
           .hvorMinstEnForekomst{fradragsberettigetTapVedRealisasjonAvFinansiellValutakontraktInngaattFom2010 erStørreEnn 0}

      val harTapFinansiellValutakontraktIUp = skattemeldingUpersonlig
          .forForekomsterAv{rederiskatteordning.finansinntektOgFinansunderskudd}
           .hvorMinstEnForekomst{fradragsberettigetTapVedRealisasjonAvFinansiellValutakontraktInngaattFom2010 erStørreEnn 0}

      val ikkeOppgittFinansinntektEllerFinansunderskuddISdf = selskapsmeldingSdf
           .forForekomsterAv{rederiskatteordning.finansinntektOgFinansunderskudd}
           .hvorMinstEnForekomst{finanskapitalandelVedUtgangenAvInntektsaaret ikkeEr oppgitt}

     val ikkeOppgittFinansinntektEllerFinansunderskuddIUp = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.finansinntektOgFinansunderskudd}
           .hvorMinstEnForekomst{finanskapitalandelVedUtgangenAvInntektsaaret ikkeEr oppgitt}

        // Gir utslag hvis innsending gjelder Upersonlig og SDF
        // Selskapet/deltaker er innenfor rederiskatteordningen og
        // Rederiskatteordning.FinansinntektOgFinansunderskudd.skattepliktigGevinstVedRealsiasjonAvFinansiellValutakontraktInngåttFom2010 er større enn null, eller  
        // Rederiskatteordning.FinansinntektOgFinansunderskudd.fradragsberettigetTapVedRealisasjohAvFinansiellValutakontraktInngåttFom2010 er større enn null) og
        // Rederiskatteordning.FinansinntektOgFinansunderskudd.finanskapitalandelVedUtgangenAvInntektsåret ikke er fylt ut 
      naeringsspesifikasjon
        .utslagFor {

        hvis((harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding og
              (harGevinstFinansiellValutakontraktIUp eller harTapFinansiellValutakontraktIUp) og
              ikkeOppgittFinansinntektEllerFinansunderskuddIUp) eller
              (harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen og
              (harGevinstFinansiellValutakontraktISdf eller harTapFinansiellValutakontraktISdf) og
              ikkeOppgittFinansinntektEllerFinansunderskuddISdf)
        )
            {
