             .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
             .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}

       val harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen = selskapsmeldingSdf
            .forForekomsterAv {rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
            .hvorMinstEnForekomst{beskatningsordning erLik "selskapHarDeltakerSomErInnenforRederibeskatningsordningen"}

       val harSkipstypeTaubaatISdf = selskapsmeldingSdf
           .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .forForekomsterAv{tonnasjeskattPerSkip}
           .hvorMinstEnForekomst{skipstype erLik "taubaat"}    

       val harSkipstypeTaubaatIUp = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .forForekomsterAv{tonnasjeskattPerSkip}
           .hvorMinstEnForekomst{skipstype erLik "taubaat"} 

       val harSkipsstatusEidSkipIkkeUnderEoesEllerIkkeOppgittISdf = selskapsmeldingSdf
          .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
           .forForekomsterAv{tonnasjeskattPerSkip}
           .hvorMinstEnForekomst{(skipsstatus erLik "skipSomErEidOgIkkeSeilerUnderEoesflaggVedUtgangenAvInntektsaaret") eller 
                                 (skipsstatus ikkeEr oppgitt)}

      val harSkipsstatusEidSkipIkkeUnderEoesEllerIkkeOppgittIUp = skattemeldingUpersonlig
          .forForekomsterAv{rederiskatteordning.tonnasjeskatt}
          .forForekomsterAv{tonnasjeskattPerSkip}
           .hvorMinstEnForekomst{(skipsstatus erLik "skipSomErEidOgIkkeSeilerUnderEoesflaggVedUtgangenAvInntektsaaret") eller 
                                 (skipsstatus ikkeEr oppgitt)}

        // Gir utslag hvis innsending gjelder Upersonlig og SDF
        // Selskapet/deltaker er innenfor rederiskatteordningen og
        // Rederiskatteordning.Tonnasjeskatt.TonnasjeskattPerSkip.Skipstype = "Taubåt" og
        // Rederiskatteordning.Tonnasjeskatt.TonnasjeskattPerSkip.Skipsstatus er lik 
        //"Eid skip som ikke seiler under EØS-flagg ved utgangen av inntektsåret" ELLER ikke er fylt ut
      naeringsspesifikasjon
        .utslagFor {

        hvis(
             (harRederibeskatningsordningBehandlesEtterSaerskilteReglerISkattemelding og
              harSkipstypeTaubaatIUp og harSkipsstatusEidSkipIkkeUnderEoesEllerIkkeOppgittIUp) eller
             (harRederibeskatningsordningBehandlesEtterSaerskilteReglerISelskapsmeldingen og
              harSkipstypeTaubaatISdf og harSkipsstatusEidSkipIkkeUnderEoesEllerIkkeOppgittISdf)
