      val harIkkeErOmfattetAvPetroleumsskattelovenUp = skattepliktUpersonligGjeldende
          .forForekomsterAv{skatteplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik false}

      val harIkkeErOmfattetAvPetroleumsskattelovenSdf = opplysningspliktSdfGjeldende
          .forForekomsterAv{opplysningsplikt}
          .hvorMinstEnForekomst{erOmfattetAvPetroleumsskatteloven erLik false}

      val harBegrensningRentefradragIKonsernOgNaerstaaendeUp = skattemeldingUpersonlig
          .hvorMinstEnForekomst{rentebegrensning er oppgitt}

      val harBegrensningRentefradragIKonsernOgNaerstaaendeSdf = selskapsmeldingSdf
         .hvorMinstEnForekomst{rentebegrensning er oppgitt}

      val harKonsernRegelverkForRentebegrensningUp = skattemeldingUpersonlig
          .forForekomsterAv{rentebegrensning}
          .hvorMinstEnForekomst{erKonsernIhtRegelverkForRentebegrensning erLik true}

      val harKonsernRegelverkForRentebegrensningSdf = selskapsmeldingSdf
          .forForekomsterAv{rentebegrensning}
           .hvorMinstEnForekomst{erKonsernIhtRegelverkForRentebegrensning erLik true}

       val harUnntaksregeltypePaaSelskapsnivaaUP = skattemeldingUpersonlig
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{anvendtUnntaksregel.unntaksregeltype erLik "unntaksregelPaaSelskapsnivaa"}

       val harUnntaksregeltypePaaSelskapsnivaaSdf = selskapsmeldingSdf
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{anvendtUnntaksregel.unntaksregeltype erLik "unntaksregelPaaSelskapsnivaa"}

       val differanseNettoRentekostnaderOgRentefradragsrammenUP = skattemeldingUpersonlig
           .forForekomsterAv{rentebegrensning}
           .hvorMinstEnForekomst{beregningsgrunnlagTilleggEllerFradragIInntekt.differanseMellomNettoRentekostnadOgRentefradragsrammen erStørreEnn 0}

       val differanseNettoRentekostnaderOgRentefradragsrammenSdf = selskapsmeldingSdf
           .forForekomsterAv{rentebegrensning}
           .hvorMinstEnForekomst{beregningsgrunnlagTilleggEllerFradragIInntekt.differanseMellomNettoRentekostnadOgRentefradragsrammen erStørreEnn 0}

       val harUnntaksregeltypePaaNasjonaltnivaaUP = skattemeldingUpersonlig
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{anvendtUnntaksregel.unntaksregeltype erLik "unntaksregelPaaNasjonaltNivaa"}

       val harUnntaksregeltypePaaNasjonaltnivaaSdf = selskapsmeldingSdf
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{anvendtUnntaksregel.unntaksregeltype erLik "unntaksregelPaaNasjonaltNivaa"}

       val harUnntakRentebegrensningHelnorskKonsernUlikJaUp = skattemeldingUpersonlig
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{anvendtUnntaksregel.erHelnorskKonsern erUlik true}

       val harUnntakRentebegrensningHelnorskKonsernUlikJaSdf = selskapsmeldingSdf
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{anvendtUnntaksregel.erHelnorskKonsern erUlik true}

       val harIkkeUnntakRentebegrensningInkludertIAnnenInnrapporteringUp = skattemeldingUpersonlig
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{anvendtUnntaksregel.selskapetErInkludertIAnnenInnrapportering erLik false}

       val harIkkeUnntakRentebegrensningInkludertIAnnenInnrapporteringSdf = selskapsmeldingSdf
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{anvendtUnntaksregel.selskapetErInkludertIAnnenInnrapportering erLik false} 

       val harIkkeEgenkapitalVedUtgangenAvRegnskapsaaretFoerInntektsaaretUp = skattemeldingUpersonlig
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{opplysningerOmSelskapsregnskapetEllerKonsolidertBalanseForNorskDelAvKonsernet.egenkapitalVedUtgangenAvRegnskapsaaretFoerInntektsaaret.beloep erLik 0}

       val harIkkeEgenkapitalVedUtgangenAvRegnskapsaaretFoerInntektsaaretSdf = selskapsmeldingSdf
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{opplysningerOmSelskapsregnskapetEllerKonsolidertBalanseForNorskDelAvKonsernet.egenkapitalVedUtgangenAvRegnskapsaaretFoerInntektsaaret.beloep erLik 0}

       val harIkkeBalansesumVedUtgangenAvRegnskapsaaretFoerInntektsaaretUp = skattemeldingUpersonlig
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{opplysningerOmSelskapsregnskapetEllerKonsolidertBalanseForNorskDelAvKonsernet.balansesumVedUtgangenAvRegnskapsaaretFoerInntektsaaret.beloep erLik 0}

       val harIkkeBalansesumVedUtgangenAvRegnskapsaaretFoerInntektsaaretSdf = selskapsmeldingSdf
           .forForekomsterAv{unntakForRentebegrensning}
           .hvorMinstEnForekomst{opplysningerOmSelskapsregnskapetEllerKonsolidertBalanseForNorskDelAvKonsernet.balansesumVedUtgangenAvRegnskapsaaretFoerInntektsaaret.beloep erLik 0}    

        // Gir utslag hvis Innsending gjelder upersonlig skattepliktig eller SDF
        // OG Tema Begrensning av rentefradrag i konsern og mellom nærstående er utfylt og
        // OG Rentebegrensning/erKonsernIhtRegelverkForRentebegrensning = "Ja" 
        // OG unntakForRentebegrensning/unntaksregeltype = "Unntaksregel på selskapsnivå" 
        //     OG Årets tillegg eller fradrag i inntekt/ differanseMellomNettoRentekostnadOgRentefradragsrammen > 0 
        //   ELLER unntakForRentebegrensning/unntaksregeltype = "Unntaksregel på nasjonalt nivå"
        //     OG unntakForRentebegrensning/erHelnorskKonsern <> "Ja" OG 
        //     OG unntakForRentebegrensning/selskapetErInkludertIAnnenInnrapportering = "Nei"
        // OG OpplysningerOmSelskapsregnskapetEllerKonsolidertBalanseForNorskDelAvKonsernet/egenkapitalVedUtgangenAvRegnskapsåretFørInntektsåret = FALSE (0) 
        //   ELLER OpplysningerOmSelskapsregnskapetEllerKonsolidertBalanseForNorskDelAvKonsernet/balansesumVedUtgangenAvRegnskapsåretFørInntektsåret = FALSE (0)
        //     (mangler info om egenkapital og balansesum i selskapsregnskapet / konsolidert balanse)
        // Skal ikke gi utslag på selskaper som omfattes av særbeskatning for petroleum     
     naeringsspesifikasjon
        .utslagFor {

     hvis((harBegrensningRentefradragIKonsernOgNaerstaaendeUp og
             harKonsernRegelverkForRentebegrensningUp og
             ((harUnntaksregeltypePaaSelskapsnivaaUP og differanseNettoRentekostnaderOgRentefradragsrammenUP) eller
              (harUnntaksregeltypePaaNasjonaltnivaaUP og harUnntakRentebegrensningHelnorskKonsernUlikJaUp og harIkkeUnntakRentebegrensningInkludertIAnnenInnrapporteringUp)) og
             harIkkeEgenkapitalVedUtgangenAvRegnskapsaaretFoerInntektsaaretUp og
             harIkkeBalansesumVedUtgangenAvRegnskapsaaretFoerInntektsaaretUp og
             harIkkeErOmfattetAvPetroleumsskattelovenUp) eller
          (harBegrensningRentefradragIKonsernOgNaerstaaendeSdf og
             harKonsernRegelverkForRentebegrensningSdf og
            ((harUnntaksregeltypePaaSelskapsnivaaSdf og differanseNettoRentekostnaderOgRentefradragsrammenSdf) eller
             (harUnntaksregeltypePaaNasjonaltnivaaSdf og harUnntakRentebegrensningHelnorskKonsernUlikJaSdf og harIkkeUnntakRentebegrensningInkludertIAnnenInnrapporteringSdf)) og
            harIkkeEgenkapitalVedUtgangenAvRegnskapsaaretFoerInntektsaaretSdf og
            harIkkeBalansesumVedUtgangenAvRegnskapsaaretFoerInntektsaaretSdf og
            harIkkeErOmfattetAvPetroleumsskattelovenSdf)
         )
              {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard)                    
               }
            }
       }

