sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når sjablongberegnet fradrag i skogbruk i fordelt beregnet næringsinntekt er ulik summen av sjablongberegnet fradrag pr tømmerkonto
        // kontrollen gir merknad i valideringstjenesten
        val sumSjablongberegnetFradragISkogbruk = naeringsspesifikasjon
            .forForekomsterAv{skogbruk.skogOgToemmerkonto}
            .summer { inntektOgKostnad.sjablongberegnetFradragKnyttetTilBiomasseOgVedproduksjon.beloep }

        naeringsspesifikasjon
            .forForekomsterAv{fordeltBeregnetNaeringsinntektForPersonligSkattepliktigEllerSdf}
            .utslagFor {
                val næringstypeSkogbruk = hvorMinstEnForekomst { naeringstype erLik "skogbruk" }

                val sumSjablongberegnetFradragFraBiomasseVedproduksjon = summer{sjablongberegnetFradragFraBiomasseVedproduksjon.beloep}

                val differanseSjablongberegnetFradrag = differanse(sumSjablongberegnetFradragFraBiomasseVedproduksjon, sumSjablongberegnetFradragISkogbruk)
                // må bruke pluss i beregningen siden sjablongberegnetInntektFraBiomasseVedproduksjon.beloepSomTall angis med minus i fordeltBeregnetNaeringsinntekt

                hvis(næringstypeSkogbruk og (differanseSjablongberegnetFradrag erStørreEnn 0)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        Avviksbeloep settesTil differanseSjablongberegnetFradrag
                    )
                }
            }
    }

