sakstype(Personlig)
    	.kontekst(ValideringSkattepliktig){
    // kontrollen gir utslag når beløpet fylt ut i feltet 'Sykepenger fisker' avviker fra innrapportert beløp. Gjelder både når beløpet er større eller mindre enn innrapportert beløp.

    utgåtteSkattemeldingFelter {
        skattemeldingUtgått
        .forForekomsterAv(sti(oppgavegiver, sykepengerOgAnnenInntektMvFraOppgavegiver))
        .utslagFor {
            val harSykepengerFisker = filtrer { behandlingsart erLik "SYKEPENGER_FISKER" }
                .hvorMinstEnForekomst { grunnlag(beloepSomGruppe.beloepSomTall) er oppgitt }

            val fastsattSykepengerFisker = filtrer { behandlingsart erLik "SYKEPENGER_FISKER" }
                .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

            val grunnlagSykepengerFisker = filtrer { behandlingsart erLik "SYKEPENGER_FISKER" }
                .summer { grunnlag(beloepSomGruppe.beloepSomTall) }

            hvis(harSykepengerFisker og ((fastsattSykepengerFisker - grunnlagSykepengerFisker) erUlik 0))
            {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadStandard,
                    Avviksbeloep settesTil (fastsattSykepengerFisker - grunnlagSykepengerFisker)
                )
            }
        }
    }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // kontrollen gir utslag når beløpet fylt ut på feltet 'Sykepenger for selvstendig næringsdrivende' avviker fra innrapportert beløp fra NAV. Gjelder både når beløpet er større eller mindre enn innrapportert beløp.

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(sti(oppgavegiver, sykepengerOgAnnenInntektMvFraOppgavegiver))
            .utslagFor {

                val harSykepengerNAV = filtrer { grunnlag(behandlingsart) erLik "SYKEPENGER_NAER" }
                    .hvorMinstEnForekomst { grunnlag(beloepSomGruppe.beloepSomTall) er oppgitt }

                val fastsattSykepengerNAV = filtrer { fastsatt(behandlingsart) erLik "SYKEPENGER_NAER" }
                    .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

                val grunnlagSykepengerNAV = filtrer { grunnlag(behandlingsart) erLik "SYKEPENGER_NAER" }
                    .summer { grunnlag(beloepSomGruppe.beloepSomTall) }

                val differanseSykepengerNAV = fastsattSykepengerNAV - grunnlagSykepengerNAV

                hvis(harSykepengerNAV og (differanseSykepengerNAV erUlik 0)) {
                    betjeningsstrategiSettesTil(
