sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
            // kontrollen gir utslag dersom skattyter i post 7830 eller 7860 har oppgitt mer enn 20 000 i kostander
    	    // AB-test på skattytere som har tallet 0, 2, 4, 6, 8 i 7.siffer i partsnummeret
            // kontrollen gir merknad i valideringstjenesten og SME
    val partsnr = skattepliktGjeldende
    	.forForekomsterAv { skatteplikt }
        .verdiFor {fastsattEllerGrunnlag(partsnummer) }

          naeringsspesifikasjon
                .forForekomsterAv { resultatregnskap.driftskostnad.annenDriftskostnad }
                .utslagFor {

                   val harAnnenDriftskostnadOverNBeloepsgrense = forForekomsterAv { kostnad }
                      .filtrer { fastsatt(type).finnesBlant("7830", "7860") }
                      .hvorMinstEnForekomst { beloep.beloep erStørreEnn 20_000 }

                    hvis( harAnnenDriftskostnadOverNBeloepsgrense og
                         (partsnr.tegnFraIndeks(7).finnesBlant("0","2","4","6","8"))) {
                        betjeningsstrategiSettesTil(
