sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig)
    .hvis({ naeringsspesifikasjon.harInnhold }) 
    {

        val sumRenteinntekt = naeringsspesifikasjon
            .forForekomsterAv { resultatregnskap.finansinntekt }
            .forForekomsterAv { inntekt }
            .filtrer { fastsatt(type) erLik "8030"}
            .summer{beloep.beloep}

        val sumRentekostnad = naeringsspesifikasjon
            .forForekomsterAv { resultatregnskap.finanskostnad }
            .forForekomsterAv { kostnad }
            .filtrer {fastsatt(type) erLik "8130"} 
            .summer{beloep.beloep}

        val sumRentekostnadOgRenteinntekt = sumRenteinntekt + sumRentekostnad

        val sumLaanAnleggsmiddel = naeringsspesifikasjon
            .forForekomsterAv { balanseregnskap.anleggsmiddel.balanseverdiForAnleggsmiddel }
            .forForekomsterAv { balanseverdi }
            .filtrer {fastsatt(type) erLik "1320"}
            .summer{beloep.beloep}

        val sumKundefordringerOmloepsmiddel = naeringsspesifikasjon
                .forForekomsterAv { balanseregnskap.omloepsmiddel.balanseverdiForOmloepsmiddel }
                .forForekomsterAv { balanseverdi }
                .filtrer { fastsatt(type) erLik "1501"}
                .summer{beloep.beloep}

        val sumGjeldLangsiktigGjeld = naeringsspesifikasjon
                .forForekomsterAv { balanseregnskap.gjeldOgEgenkapital.langsiktigGjeld }
                .forForekomsterAv { gjeld }
                .filtrer {fastsatt(type) erLik "2260"}
                .summer{beloep.beloep}

        val sumLeverandoergjeldKortsiktigGjeld = naeringsspesifikasjon
                .forForekomsterAv { balanseregnskap.gjeldOgEgenkapital.kortsiktigGjeld }
                .forForekomsterAv { gjeld }
                .filtrer {fastsatt(type) erLik "2460"}
                .summer{beloep.beloep}

         val sumLaanogGjeld = sumLaanAnleggsmiddel +  sumKundefordringerOmloepsmiddel + sumGjeldLangsiktigGjeld + sumLeverandoergjeldKortsiktigGjeld

        // Kontrollen gir utslag hvis 
        // 8030 Renteinntekt fra foretak i samme konsern (finansinntekt) + 8130 Rentekostnad til foretak i samme konsern (finanskostnad) > 10 mill.  
        //    eller 1320 Lån til foretak i samme konsern (anleggsmiddel) 
        //       + 1501 Kundefordringer på foretak i samme konsern (Omløpsmiddel) 
        //       + 2260 Gjeld til foretak i samme konsern (langsiktig gjeld) 
        //       + 2460 Leverandørgjeld til foretak i samme konsern (kortsiktig gjeld) > 25 mill. 
        // og temaet i skattemelding UPS "Kontrollerte transaksjoner/disposisjoner og mellomværender" mangler 
        //eller valgt "Nei" for unntak i Har unntak fra plikten til å levere temakortet "Kontrollerte transaksjoner/disposisjoner og mellomværender". 
        skattemeldingUpersonlig
          .utslagFor {

        val harAvkreftetUnntakFraPliktTilAaLevere = forForekomsterAv{oppgaveOgDokumentasjonsplikt}
           .hvorMinstEnForekomst{(harKontrollertTransaksjonDisposisjonOgMellomvaerendeOverBeloepsgrense ikkeEr oppgitt) eller
                                 (harKontrollertTransaksjonDisposisjonOgMellomvaerendeOverBeloepsgrense erLik false)}


            hvis( ((sumRentekostnadOgRenteinntekt erStørreEnn 10_000_000) eller 
                   (sumLaanogGjeld erStørreEnn 25_000_000)) og
                   harAvkreftetUnntakFraPliktTilAaLevere
            ) {
                betjeningsstrategiSettesTil(
                    ikke,
                    merknadStandard
                )
            }
        }
    }

