sakstype(Upersonlig)
    .kontekst(ValideringSkattepliktig) {

        val partsnr = skattepliktUpersonligGjeldende	
           .verdiFor { skattepliktOgOpplysningerOmSkattesubjekt.partsnummer }

        val finnesIKontrollisteFusjonFisjonInnsats = finnesIKontrolliste(
           "Underskudd fusjonFisjonInnsats",
           listeAv(partsnr))

        val gjeldsettergivelseIEgenkapitalavstemming = naeringsspesifikasjon
            .forForekomsterAv{egenkapitalavstemming}
            .forForekomsterAv{egenkapitalendring}
            .filtrer { fastsatt(egenkapitalendringstype) erLik "gjeldsettergivelse" }
            .summer { fastsatt(beloep.beloep) }

        val selskapIStorbedrift = ansvarligEnhet
      		.hvorMinstEnForekomst{erStorbedrift erLik true}     

        val rederibeskatningsordningBehandlesEtterSaerskilteRegler = skattemeldingUpersonlig
           .forForekomsterAv{rederiskatteordning.spesifikasjonAvSkattleggingEtterSaerskiltBeskatningsordning}
           .hvorMinstEnForekomst{beskatningsordning erLik "behandlesEtterSaerskilteRederibeskatningsreglerIInntektsaaret"}    

        // kontrollen gir utslag dersom  Gjeldsettergivelse oppført under Egenkapitalavstemming er større enn «Oppnådd underhåndsakkord og gjeldsettergivelse for Inntekt og underskudd» i SME
        // og Selskapet må ha fastsatt "Fremført underskudd fra tidligere år"
        // skal ikke gi utslag på storbedrift og rederi
        // skal IKKE gi utslag hvis skattyter ligger i listen "Underskudd fusjonFisjonInnsats"
        skattemeldingUpersonlig
            .utslagFor {
                val gjeldsettergivelseiSME = forForekomsterAv{inntektOgUnderskudd}
                    .summer { fastsatt(underskuddTilFremfoering.oppnaaddUnderhaandsakkordOgGjeldsettergivelse) }

                val underskuddTilfremfoering = forForekomsterAv{inntektOgUnderskudd}
                    .summer { fastsatt(underskuddTilFremfoering.fremfoertUnderskuddFraTidligereAar) }

                val avvikGjeldsettergivelse = gjeldsettergivelseIEgenkapitalavstemming - gjeldsettergivelseiSME 

                hvis(ikke(selskapIStorbedrift) og
                     ikke(rederibeskatningsordningBehandlesEtterSaerskilteRegler) og
                     (avvikGjeldsettergivelse erStørreEnn 0) og
                     (underskuddTilfremfoering erStørreEnn 0) og
                    ikke(finnesIKontrollisteFusjonFisjonInnsats)) {
                   betjeningsstrategiSettesTil(
                    ikke, merknadStandard
                    )
                }
            }
    }

