sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        // hvis sluttbruker har lagt inn pendleropphold
        // og IKKE har lagt inn land for arbeidssted eller pendlerbolig
        //      eller mangler sted for utenlandsk bolig eller sted 
        //      eller mangler kommune for norsk bolig eller sted 
       skattemelding
            .forForekomsterAv{pendleropphold}
            .utslagFor {

                val harOppgittPendleroppholdArbeidsstedUtenLandKommune = forForekomsterAv{spesifikasjonAvArbeidssted}
                   .hvorMinstEnForekomst {
                     (fastsatt(arbeidsstedsland) ikkeEr oppgitt) eller
                     ((fastsatt(arbeidsstedsland) erLik "NO") og  (fastsatt(arbeidsstedskommune) ikkeEr oppgitt)) eller
                     ((fastsatt(arbeidsstedsland) er oppgitt) og (fastsatt(arbeidsstedsland) erUlik "NO") og (fastsatt(arbeidssted) ikkeEr oppgitt))
                   }

                val harOppgittPendleroppholdPendlerboligUtenLandKommune = forForekomsterAv{spesifikasjonAvPendlerbolig}
                   .hvorMinstEnForekomst {
                     (fastsatt(landForPendlerbolig) ikkeEr oppgitt) eller 
                     ((fastsatt(landForPendlerbolig) erLik "NO") og (fastsatt(kommuneForPendlerbolig) ikkeEr oppgitt)) eller
                     ((fastsatt(landForPendlerbolig) er oppgitt) og (fastsatt(landForPendlerbolig) erUlik "NO") og (fastsatt(stedForPendlerbolig) ikkeEr oppgitt)) 
                   }   

                hvis(harOppgittPendleroppholdArbeidsstedUtenLandKommune eller
                     harOppgittPendleroppholdPendlerboligUtenLandKommune) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )
                }
            }
