            .utslagFor {
                val harOppgittOpptjentIUtlandetSomEndringsaarsak = forForekomsterAv(aarsakTilEndring)
                    .hvorMinstEnForekomst { aarsak erLik "endretTilOpptjentIUtlandet" }

                hvis(harOppgittOpptjentIUtlandetSomEndringsaarsak) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
