sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

         // Skattepliktig har redusert beløp i preutfylt pensjonskort med utbetaler NAV
         // og skattepliktig har lagt til felt "opptjeningsland" Thailand på et pensjonskort
         // skal ikke gi utslag hvis kort for avtalebosted under tema Andre forhold er lagt til
         // skal ikke gi utslag hvis skattepliktig har skatteplikt Begrenset
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { fastsatt(landkode) erLik "TH" }
            .utslagFor {
                val sumPensjonPerUtbetaler = forForekomsterAv(perMaaned)
                    .summer { pensjonsinntektSomGruppe.beloepSomTall }

                val pensjonGrunnlagMinusFastsatt =
                    grunnlag(sumPensjonPerUtbetaler) - fastsatt(sumPensjonPerUtbetaler)

                val bosattland =
                    skattemeldingUtgått
                        .forForekomsterAv(tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale)
                        .hvorMinstEnForekomst {
                            (bosattILandEtterSkatteavtale er oppgitt)
                        }

                hvis(
                    ikke(harInnsending) og ikke(harBegrensetSkatteplikt) og ikke(bosattland) og
                        (pensjonGrunnlagMinusFastsatt erStørreEnn 0)
                )
                {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
      .kontekst(ValideringSkattepliktig) {
          val harUtslagPaaKontroll = utslagPåKontroll("PENSJON_NAV_ENDRET_TIL_THAILAND_AVTALEBOSTED") eller
                                     utslagPåKontroll("PENSJON_NAV_ENDRET_TIL_SPANIA_AVTALEBOSTED")

    // skal gi Tips hvis det er utslag på PENSJON_NAV_ENDRET_TIL_THAILAND_AVTALEBOSTED eller PENSJON_NAV_ENDRET_TIL_SPANIA_AVTALEBOSTED.

     utgåtteSkattemeldingFelter {
         skattemeldingUtgått
         .utslagFor {

         hvis(
             harUtslagPaaKontroll
         )
         {
             betjeningsstrategiSettesTil(ikke, merknadTips, 1)
         }
     }
     }
      }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harUtslagPaaKontroll = utslagPåKontroll("KPP_REDUKSJON_AV_PREUTFYLT_BELØP") eller
                                       utslagPåKontroll("AVTALEBOSTED_PENSJON_REDUSERT_THAILAND")

         // kontrollen gir utslag og merknad dersom skattyter reduserer preutfylt pensjon
        // og skattyter ikke har utslag på kontrollen KPP_REDUKSJON_AV_PREUTFYLT_BELØP eller
        // utslag på AVTALEBOSTED_PENSJON_REDUSERT_THAILAND
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { grunnlag(utbetalersNavn) er oppgitt }
            .utslagFor {
                val sumPensjonPerUtbetaler = forForekomsterAv(perMaaned)
                    .summer { pensjonsinntektSomGruppe.beloepSomTall }

                val pensjonGrunnlagMinusFastsatt =
                    grunnlag(sumPensjonPerUtbetaler) - fastsatt(sumPensjonPerUtbetaler)

                hvis(
                    ikke(harInnsending) og (pensjonGrunnlagMinusFastsatt erStørreEnn 0) og ikke(
                        harUtslagPaaKontroll
                    )
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
     .kontekst(ValideringSkattepliktig) 
     .hvis({
          val erKPP = skattepliktGjeldende
             .forForekomsterAv { skatteplikt }
             .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
             .hvorMinstEnForekomst { type erLik "kildeskattepliktPaaPensjon" }

          ikke(harInnsending) og ikke(erKPP)
     })
    {

         // kontrollen gir utslag og merknad dersom skattyter har endret preutfylt pensjon/trygd med mer enn 100
         //skal ikke gi utslag på KPP (er egen kontroll for det)
         skattemelding
             .forForekomsterAv{pensjonsinntekt}
             .filtrer { grunnlag(utbetalersNavn) er oppgitt }
             .utslagFor {
                 val sumPensjonTrygdPerUtbetaler = forForekomsterAv{perMaaned}
                     .summer { pensjonsinntekt.beloep }

                 val differansePensjonTrygd =
                     differanse(grunnlag(sumPensjonTrygdPerUtbetaler), fastsatt(sumPensjonTrygdPerUtbetaler))

                 hvis(ikke(harInnsending) og 
                      (differansePensjonTrygd erStørreEnn 100)) {
                     betjeningsstrategiSettesTil(ikke, merknadInformasjon)
                 }
             }
         }

