sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        // hvis sluttbruker har lagt inn sum reisefradrag
        // og ikke har lagt spesifikasjon av hverken arbeidsreise eller pendler/besøksreise innenfor eller utenfor EØS
       skattemelding
            .forForekomsterAv{reiserIArbeidsforhold}
            .utslagFor {

                val harOppgittReisefradrag = hvorMinstEnForekomst{
                  (fastsatt(reisefradrag.fradragsberettigetBeloep.beloep) erStørreEnn 0) eller 
                  (fastsatt(reisefradrag.beloepUtenHensynTilValgtPrioritertFradragstype.beloep) erStørreEnn 0)
                }

                val harIkkeOppgittspesifikasjonAvArbeidsEllerBesoeksreise = harIngenForekomsterDer{
                  (arbeidsreise er oppgitt) eller
                  (besoeksreiseTilHjemInnenforEOeS er oppgitt) eller
                  (besoeksreiseTilHjemUtenforEOeS er oppgitt)
                }

                hvis(harOppgittReisefradrag og harIkkeOppgittspesifikasjonAvArbeidsEllerBesoeksreise) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen skal gi utslag og merknad dersom skattyter har oppgitt arbeidsreise eller besøæksreise
        // uten å ha oppgitt lønn eller næringsspesifikasjon eller opplysninger om deltakers andel i SDF
        val harOppgittNaeringsspesifikasjon = naeringsspesifikasjon
            .hvorMinstEnForekomst { naeringsspesifikasjon.partsreferanse er oppgitt }

        val harIKkeOppgittNaeringsInfoISkattemeldingen = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.harIngenForekomsterDer { fastsatt(naeringsinntektMv) er oppgitt }
        }

        val harIkkeOppgittDeltakerinformasjonSDF = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.harIngenForekomsterDer { fastsatt(deltakersAndelAvFormueOgInntekt) er oppgitt }
        }

        val harIkkeOppgittLoenn = utgåtteSkattemeldingFelter {
            skattemeldingUtgått.harIngenForekomsterDer { fastsatt(arbeidsgiver) er oppgitt }
        }

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(reiserIArbeidsforhold)
            .forForekomsterAv(
                gruppe(
                    arbeidsreise,
                    besoeksreiseTilHjemInnenforEOeS,
                    besoeksreiseTilHjemUtenforEOeS
                )
            )
            .utslagFor {
                hvis(
                    (antallReiser erStørreEnn 0) og
                        (ikke(harOppgittNaeringsspesifikasjon) og harIKkeOppgittNaeringsInfoISkattemeldingen og harIkkeOppgittDeltakerinformasjonSDF og harIkkeOppgittLoenn)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        // hvis sluttbruker har lagt inn info om arbeidsreise mellom hjem og arbeid og ikke har lagt inn adresse for arbeidssted eller hjemsted (obligatoriske felter i SME)
       skattemelding
            .forForekomsterAv{reiserIArbeidsforhold}
            .utslagFor {

                val harOppgittarbeidsreiseMedMangelfullAdresse = forForekomsterAv{arbeidsreise}
                   .hvorMinstEnForekomst {
                      ((fastsatt(antallReiser) er oppgitt) eller
                      (fastsatt(lengdeAvReiseTurRetur) er oppgitt) eller
                      (fastsatt(egenandelVedReiseDekketAvArbeidsgiver.beloep) er oppgitt) eller
                      (fastsatt(sparerMerEnnToTimerVedBrukAvEgenBil) er oppgitt) eller
                      (fastsatt(bompengeutgift.beloep) er oppgitt) eller
                      (fastsatt(fergeutgift.beloep) er oppgitt)) og 
                      ikke((fastsatt(reiseFraAdresse) er oppgitt) og (fastsatt(reiseFraSted) er oppgitt) og
                           (fastsatt(reiseTilAdresse) er oppgitt) og (fastsatt(reiseTilSted) er oppgitt)) }

                hvis(harOppgittarbeidsreiseMedMangelfullAdresse) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
       val harUtslagPaaKontroll = utslagPåKontroll("SÆRFRADRAG_ERVERVSEVNE_TRYGDET") eller
                                       utslagPåKontroll("SÆRFRADRAG_ERVERVSEVNE_HØY_INNTEKT_FORMUE") 

     // kontrollen gir utslag dersom skattyter krever særfradrag for lettere nedsatt ervervsevne
     // Skal ikke gi utslag hvis utslag på SÆRFRADRAG_ERVERVSEVNE_TRYGDET og SÆRFRADRAG_ERVERVSEVNE_HØY_INNTEKT_FORMUE 
     skattemelding
        .utslagFor {

        val harSaerfradragForLettereNedsattErvervsevne = forForekomsterAv{saerfradragForLettereNedsattErvervsevne}
           .hvorMinstEnForekomst { (fastsatt(kreverSaerfradrag) erLik true) }

        hvis(ikke(harUtslagPaaKontroll) og harSaerfradragForLettereNedsattErvervsevne) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        // kontrollen gir utslag dersom sluttbruker endrer BSU til ulik grunnlag
        skattemelding
            .forForekomsterAv{boligsparingForUngdom}
            .utslagFor {
                val differanseFradrag =
                     hvorMinstEnForekomst {
                     grunnlag(beregnetSparebeloepTilBoligsparingForUngdomISkattleggingsperioden.fradragsberettigetBeloep.beloep) erUlik
                     fastsatt(beregnetSparebeloepTilBoligsparingForUngdomISkattleggingsperioden.fradragsberettigetBeloep.beloep)
                }
                hvis(differanseFradrag)
              {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        // kontrollen gir utslag dersom sluttbruker har lagt til pensjonsytelse og ikke har forekomst av "pensjonsinntektPerMaaned"
        skattemelding
            .forForekomsterAv{pensjonsinntekt}
            .utslagFor {

                val harOppgittKortFortPensjonsinntekt = hvorMinstEnForekomst{utbetalersNavn er oppgitt}
                val harIkkeOpgittPensjonsinntektPerMaaned = harIngenForekomsterDer{perMaaned er oppgitt}

                hvis(harOppgittKortFortPensjonsinntekt og harIkkeOpgittPensjonsinntektPerMaaned) {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringFoerBeregning) {

        // kontrollen gir utslag dersom sluttbruker legger inn Fradrag for investering i oppstartsbedrifter uten at det er grunnlagsdata
        skattemelding
            .forForekomsterAv{fradragForInvesteringIOppstartsselskap}
            .utslagFor {
                val differanseFradrag = hvorMinstEnForekomst {
                     (grunnlag(fradragsberettigetBeloep.beloep) erLik 0) og
                     (fastsatt(fradragsberettigetBeloep.beloep) erStørreEnn 0)
                }
                hvis(differanseFradrag)
              {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val partsnr = skattepliktGjeldende
            .forForekomsterAv{skatteplikt}
            .verdiFor { partsnummer }

         val harUtslagPaaKontroll = utslagPåKontroll("EIENDOM_UTLEIE_I_FJOR")        

         val harUtvidetVeiledningPaaUtleie = utvidetVeiledning
             .forForekomsterAv { utvidetveiledning }
             .forForekomsterAv { metadata }
             .hvorMinstEnForekomst {type.finnesBlant("UTLEIE_AV_FAST_EIENDOM", "UTLEIE_SEKUNDAERBOLIGMODELL") }

         val harSekundaerboligSelveidEllerBoligselskap = skattemelding
             .forForekomsterAv{grupper(formuesgrunnlagSelveidBolig, 
                                       formuesgrunnlagBoenhetIBoligselskap)}
             .hvorMinstEnForekomst { boligbruk erLik "sekundaerbolig" }

         val harSekundaerboligFlerboligbygg = skattemelding
             .forForekomsterAv{formuesgrunnlagFlerboligbygning}
             .forForekomsterAv{useksjonertBoenhet}
             .hvorMinstEnForekomst { boligbruk erLik "sekundaerbolig" }

        // kontrollen gir utslag dersom skattyter har sekundærbolig, men mangler utleie
        // Skal ikke gi utslag hvis skattepliktig har fått produsert utvidet veileding (og vist det eller et tilhørende tips) 
        // skal heller ikke gi utslag dersom skattyter har utslag på EIENDOM_UTLEIE_I_FJOR
        //AB-test på skattepliktige som har tallet 2, 3, 4, 5, 6, 7, 8, 9 i 7. siffer i partsnummeret
        skattemelding
            .utslagFor {
               val harUtleieSjablongbehandlet = forForekomsterAv{grupper(sjablongbehandletUtleie,
                                                                         sjablongOgRegnskapsbehandletUtleie)
               }
                  .hvorMinstEnForekomst {fastsatt(bruttoLeieinntektVedSjablongbehandletUtleie.beloep) erStørreEnn 0}

               val harUtleieRegnskapsbehandlet = forForekomsterAv{grupper(regnskapsbehandletUtleie,
                                                                          sjablongOgRegnskapsbehandletUtleie) 
               }
                  .hvorMinstEnForekomst {fastsatt(bruttoLeieinntektVedRegnskapsbehandletUtleie.beloep) erStørreEnn 0}    

            hvis(
                (partsnr.tegnFraIndeks(7).finnesBlant("2","3","4","5","6","7","8","9")) og
                ikke(harUtslagPaaKontroll) og
                ikke(harUtvidetVeiledningPaaUtleie) og
                (harSekundaerboligSelveidEllerBoligselskap eller harSekundaerboligFlerboligbygg) og
                ikke(harUtleieSjablongbehandlet) og
                ikke(harUtleieRegnskapsbehandlet)
            )
            {
                betjeningsstrategiSettesTil(ikke, merknadTips, skjulerSkatteberegning)
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) 
    .importer(ER_SJØ_ELLER_SOKKEL) 
     { 
      (D_erSjoeEllerSokkel) ->

        val erSjoeEllerSokkel = skattepliktGjeldende.D_erSjoeEllerSokkel()

        // Gir utslag på de som er sjø eller sokkel i skatteplikt
        // Og har eiendom egenfastsatt eller preutfylt i utlandet
        skattemelding
            .forForekomsterAv{fastEiendom}
            .utslagFor {

                val harEiendomIUtlandet = hvorMinstEnForekomst{(fastsatt(landkodeBeliggenhet) er oppgitt) og 
                                                               (fastsatt(landkodeBeliggenhet) erUlik "NO")}


                hvis(erSjoeEllerSokkel og 
                     harEiendomIUtlandet
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val erSjoeEllerSokkel = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst {
                type.finnesBlant(
                    "skattepliktSomSjoemann",
                    "skattepliktEtterPetroleumsskatteloven"
                )
            }

        //kontrollen gir utslag og merknad dersom skattyter har innrapporteringer med utenlandsk lønn som har trygde- eller skattemessig unntak
        //kontrollen skal kun gi utslag på skattytere som er sjø/sokkel i skatteplikt
        skattemelding
            .forForekomsterAv{arbeidsgiver}
            .utslagForFørste {
            val sumUtenlandskLoennMedUnntak = forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
                .filtrer {
                    (
                        grunnlag(behandlingsart).finnesBlant(
                            "LONN_UTL",
                            "NETTOLONN_UTL",
                            "NATURALYTELSE_UTL",
                            "BONUS_FERIEP"
                        )
                        ) og
                        (grunnlag(beloep.trygdeEllerSkattemessigUnntak) er oppgitt)
                }
                .summer { grunnlag(beloep.beloep) }

            hvis(erSjoeEllerSokkel og (sumUtenlandskLoennMedUnntak erStørreEnn 0))
            {
                betjeningsstrategiSettesTil(
                    ikke, merknadInformasjon
                )
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val erSjoeEllerSokkel = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst {
                type.finnesBlant(
                    "skattepliktSomSjoemann",
                    "skattepliktEtterPetroleumsskatteloven"
                )
            }

        //kontrollen gir utslag og merknad dersom skattyter har innrapporteringer fra nav
        //og trygde- eller skattemessig unntak er oppgitt
        //skal bare gi utslag dersom skattyter er sjø eller sokkel
        //Skal bare gi et utslag
        skattemelding
            .utslagForFørste {
            val sumInnrapportertfraNAVMedUnntak = forForekomsterAv{arbeidsgiver}
                .forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
                .filtrer {
                    (
                        behandlingsart.finnesBlant(
                            "DAGPENGER",
                            "SYKEPENGER",
                            "AAP",
                            "SVANGERSKAPSPENGER",
                            "DAGP_NAERING",
                            "SYKEPENGER_NAER"
                        )
                        ) og
                        (fastsatt(beloep.trygdeEllerSkattemessigUnntak) er oppgitt)
                }
                .summer { fastsatt(beloep.beloep) }

            hvis(erSjoeEllerSokkel og (sumInnrapportertfraNAVMedUnntak erStørreEnn 0))
            {
                betjeningsstrategiSettesTil(
                    ikke, merknadInformasjon)
            }
        }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag og merknad dersom skattyter har skattefri inntekt OG har oppgitt dobbeltbeskatningsmetode kredit
        // skal ikke gi utslag dersom metodeVedDobbeltbeskatning eller trygdeEllerSkattemessigUnntak er myndighetsfastsatt
        skattemelding
            .forForekomsterAv{arbeidsgiver}
            .utslagFor {
                val harSkattefriInntektMedDobbeltbekstaningsmetodeKredit =
                    forForekomsterAv{samledeYtelserFraArbeidsgiverPerBehandlingsart}
                        .hvorMinstEnForekomst {
                            (fastsatt(beloep.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriKildeskattPaaPensjon",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattefriUtenTrygdeavgiftsplikt")) og
                            (fastsatt(beloep.metodeVedDobbeltbeskatning) erLik "kredit") og
                            (fastsatt(beloep.trygdeEllerSkattemessigUnntak) ikkeEr myndighetsfastsatt) og 
                            (fastsatt(beloep.metodeVedDobbeltbeskatning) ikkeEr myndighetsfastsatt)
                        }
                hvis(harSkattefriInntektMedDobbeltbekstaningsmetodeKredit) {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
       val harUtslagPaaKontroll = utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_FRADRAG_FOR_DOBBELTBESKATNING")          

        // kontrollen gir utslag og merknad dersom skattyter har skattefri inntekt OG har oppgitt dobbeltbeskatningsmetode kredit (utslag på annen kontroll)
        // og har oppgitt tilleggsinfo for kreditfradrag/skatt betalt i utlandet
        // merknaden skal vises på tilleggsinfokortet
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{kravOmKreditfradragForUtenlandskSkatt}
            .utslagForFørste {
                val harOppgittTilleggsinfoOmKreditfradrag = hvorMinstEnForekomst{fastsatt(landkode) er oppgitt}                    

                hvis(harUtslagPaaKontroll og
                    harOppgittTilleggsinfoOmKreditfradrag) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag og merknad dersom skattyter har skattefri inntekt OG har oppgitt dobbeltbeskatningsmetode alt fordeling eller ettårsregel
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(arbeidsgiver)
            .utslagFor {
                val harSkattefriInntektMedDobbeltbeskatningsmetodeSkattenedsettelse =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (
                                fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriKildeskattPaaPensjon",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                ) og
                                (
                                    fastsatt(beloepSomGruppe.metodeVedDobbeltbeskatning).finnesBlant(
                                        "denAlternativeFordelingsmetoden",
                                        "ettaarsregelen"
                                    )
                                    )
                        }
                hvis(harSkattefriInntektMedDobbeltbeskatningsmetodeSkattenedsettelse) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
      val harUtslagPaaKontroll = utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_SKATTENEDSETTELSE_FOR_DOBBELTBESKATNING")          

      // kontrollen gir utslag og merknad dersom skattyter har skattefri inntekt OG har oppgitt dobbeltbeskatningsmetode alternativ fordeling eller ettårsregelen (utslag på annen kontroll)
      // og har oppgitt tilleggsinfo for alternativ fordeling eller ettårsregelen
      // merknaden skal vises på tilleggsinfokortet
      skattemelding
        .fokusområde("Fokusområde")
        .utslagFor {
          val harOppgittTilleggsinfoOmDenAlternativeFordelingsmetoden = forForekomsterAv{tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden}
            .hvorMinstEnForekomst{startdatoForOppholdIUtland er oppgitt}   

          val harOppgittTilleggsinfoOmEttaarsregelen = forForekomsterAv{tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen}
            .forForekomsterAv{arbeidsforholdIUtlandet}
            .hvorMinstEnForekomst{startdato er oppgitt}    

          hvis(harUtslagPaaKontroll og
               (harOppgittTilleggsinfoOmDenAlternativeFordelingsmetoden eller harOppgittTilleggsinfoOmEttaarsregelen) ) {
            betjeningsstrategiSettesTil(ikke, merknadStandard)
          }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv{skatteplikt}
            .hvorMinstEnForekomst{personligSkattesubjekt.skattepliktTilNorge erLik "begrenset"}

        val harUtslagPaaKontroll = utslagPåKontroll("EMIGRASJON_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD") eller
                                   utslagPåKontroll("AVTALEBOSTED_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD") eller
                                   utslagPåKontroll("GLOBAL_PÅSTAND_KPP") 

        val tilllegsinfoVedSkattemessigUtflytting = skattemelding
            .forForekomsterAv{tilleggsinformasjonVedSkattemessigUtflytting}                           

        // Kontrollen gir utslag og merknad dersom skattyter har oppgitt endringsårsak = skattemessigEmigrasjon på minst et kort
        // og ikke har oppgitt tilhørende tilleggsinformasjon under andre forhold
        // skal ikke gi utslag dersom utslag på gitte kontroller
        // skal IKKE gi utslag hvis skatteplikt Begrenset
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {
            val kortMedEndringsaarsak = forAlleForekomster
                .hvorMinstEnForekomst { aarsak erLik "skattemessigEmigrasjon" }

            hvis(ikke(harBegrensetSkatteplikt) og
                ikke(harUtslagPaaKontroll) og
                    kortMedEndringsaarsak og
                    (ikke(tilleggsinformasjonVedSkattemessigUtflytting er oppgitt))
            ) {
                betjeningsstrategiSettesTil(ikke, merknadStandard, 
                                            Lenke settesTil tilllegsinfoVedSkattemessigUtflytting)
            }
        }
        }
    }

