sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        val harUtslagPaaKontroll = utslagPåKontroll("EMIGRASJON_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD") eller
                                   utslagPåKontroll("AVTALEBOSTED_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD") eller
                                   utslagPåKontroll("SKATTEFRI_INNTEKT_INGEN_SKATTENEDSETTELSE_FOR_DOBBELTBESKATNING")

        // Kontrollen gir utslag og merknad dersom skattyter har oppgitt dobbeltbeskatningsmetode = ettaarsregelen på lønn
        // og ikke har oppgitt tilhørende tilleggsinformasjon under andre forhold (tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen)
        // skal ikke gi utslag dersom utslag på gitte kontroller

        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .fokusområde("Fokusområde")
            .forForekomsterAv(arbeidsgiver)
            .utslagFor {

                val harOppgittEttaarsregelLoenn =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst { beloepSomGruppe.metodeVedDobbeltbeskatning erLik "ettaarsregelen" }


                hvis(
                    ikke(harUtslagPaaKontroll) og
                        harOppgittEttaarsregelLoenn og
                        (tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen ikkeEr oppgitt)
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard)
                }
            }
        }
    }

sakstype(Personlig)
