sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val periodelagerforekomster = periodelager

        val harOppholdsperiode = periodelagerforekomster
            .forForekomsterAv { totalPerioder }
            .hvorMinstEnForekomst{ (antallDagerMedOppholdsperioderUtenSvalbard erStørreEnn 0) eller
                                   (antallDagerMedOppholdsperioderGjelderSvalbard erStørreEnn 0) }

        val arbeidsforholdsforekomster = periodelagerforekomster
            .forForekomsterAv { perioder }
            .forForekomsterAv { arbeidsforholdsperiode }

        val harArbeidsforholdPaaFastland = arbeidsforholdsforekomster
            .hvorMinstEnForekomst {arbeidsstedsbeliggenhet erLik "fastland"}

        val harArbeidsforholdPaaSokkelSjoe = arbeidsforholdsforekomster
            .hvorMinstEnForekomst {arbeidsstedsbeliggenhet.finnesBlant("kontinentalsokkel", "sjoe")}      

        val skattepliktforekomster = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }

        val harBegrensetSkatteplikt = skattepliktforekomster
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val harSaerskiltSkatteplikt = skattepliktforekomster
            .forForekomsterAv { personligSkattesubjekt.saerskiltSkatteplikt }
            .hvorMinstEnForekomst { type er oppgitt }      

        //kontrollen gir utslag dersom skattepliktig har begrenset skatteplikt
        //og skattyter har arbeidsforhold både på land og sjø/sokkel
        //og skattyter krever standardfradrag
        //og IKKE har særskilt skatteplikt 
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(standardfradrag)
            .utslagFor {

                val harKravOmStandardfradrag =
                    hvorMinstEnForekomst { fastsatt(kreverStandardfradrag) er oppgitt }


                hvis(
                    harBegrensetSkatteplikt og
                        ikke(harSaerskiltSkatteplikt) og
                        harArbeidsforholdPaaFastland og harOppholdsperiode og
                        harArbeidsforholdPaaSokkelSjoe og
                        harKravOmStandardfradrag
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon
                    )
                }
            }
        }
    }

