           .forForekomsterAv { skatteplikt }
           .hvorMinstEnForekomst{((personligSkattesubjekt.skattepliktTilSvalbard erUlik "ingenSkatteplikt") og (personligSkattesubjekt.skattepliktTilSvalbard er oppgitt)) eller
                                ((doedsbo.skattepliktTilSvalbard erUlik "ingenSkatteplikt") og (doedsbo.skattepliktTilSvalbard er oppgitt))}

        // Kontrollen gir utslag og merknad dersom skattyter har skatteplikt til Svalbard og
        // har pensjon/trygd (ytelser fra NAV) på Svalbard (kommune 2100)
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forForekomsterAv(pensjonsinntekt)
            .filtrer {
                (grunnlag(kommunenummer) erLik "2100") og
                    (grunnlag(pensjonstype).finnesBlant(
                        "AFP",
                        "ALDERSP_FOLKETR",
                        "BARNEP_FOLKETR",
                        "EFTILLEGG",
                        "ETTERLATTEPENSJ",
                        "FODERAD",
                        "INTROD_STONAD",
                        "IPA_IPS_BARNEP",
                        "IPA_IPS_ENGANGS",
                        "IPA_IPS_PERIODISK",
                        "IPA_IPS_UFOREP",
                        "KRIGSPENSJON",
                        "KVALIF_STONAD",
                        "NY_AFP",
                        "NYE_LIVRENTER",
                        "OVERGANGSSTONAD",
                        "PENSJON_DIVERSE",
                        "PENSJON_NATURALYT",
                        "RENTEFORDEL_PENSJ",
                        "SKFRIE_YTELSER",
                        "SKFRITT_EF_TILLEGG",
                        "SLITERTILLEGG",
                        "SUPPL_STONAD",
                        "SUPPL_STONAD_UFOR",
                        "UFOREP_FOLKETR"
                    ))
            }
            .utslagForFørste {

                val pensjonTrygdPaaSvalbard =
                    hvorMinstEnForekomst { grunnlag(pensjonsinntektPrSkattleggingsperiode.beloepSomTall) erStørreEnn 0 }


                hvis(erSkattepliktigTilSvalbard og pensjonTrygdPaaSvalbard)
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon
                    )
                }
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val harSkattepliktTilSvalbard = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst {
                personligSkattesubjekt.skattepliktTilSvalbard.finnesBlant(
                    "global",
                    "begrenset", "skattepliktUtenOpphold"
                )
            }

        //kontrollen gir utslag dersom skattyter som har skatteplikt til Svalbard
        //og har gjeld eller gjeldsrenter med kommunenr 2100 (Svalbard)
        //og samtidig har eiendom i Norge (ikke Svalbard eller utlandet)
        utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .utslagFor {

            val harGjeldGjeldsrentePaaSvalbard = forForekomsterAv(konto)
                .hvorMinstEnForekomst {
                    (kommunenummer erLik "2100") og
                        ((gjeld.beloepSomTall erStørreEnn 0) eller (paaloepteRenter.beloepSomTall erStørreEnn 0))
                }

            val harEiendomINorge = forForekomsterAv(fastEiendom)
                .hvorMinstEnForekomst { (landkode ikkeEr oppgitt) eller (landkode erLik "NO") }

            hvis(harSkattepliktTilSvalbard og harGjeldGjeldsrentePaaSvalbard og harEiendomINorge) {
                betjeningsstrategiSettesTil(
                    ikke, merknadInformasjon, 1
                )
            }
        }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
    {
        val harForekomstMedKommunenrSvalbard = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forAlleForekomster
            .hvorMinstEnForekomst { fastsatt(kommunenummer) erLik "2100" }
        }

        val harForekomsterMedLandkodeSvalbard = utgåtteSkattemeldingFelter {
            skattemeldingUtgått
            .forAlleForekomster
            .hvorMinstEnForekomst { fastsatt(landkode) erLik "SJ" }
        }

        //kontrollen gir utslag dersom skatepliktig i gjeldende skatteplikt ikke har skatteplikt til Svalbard (hverken ordinær eller dødsbo)
        //og skattepliktig registrerer opplysninger som gjelder Svalbard
        skattepliktGjeldende
            .utslagFor {
                val harIkkeSkattepliktTilSvalbard = forForekomsterAv { skatteplikt }
                    .hvorMinstEnForekomst {
                        ((personligSkattesubjekt.skattepliktTilSvalbard erLik "ingenSkatteplikt") eller (personligSkattesubjekt.skattepliktTilSvalbard ikkeEr oppgitt)) og
                            ((doedsbo.skattepliktTilSvalbard erLik "ingenSkatteplikt") eller (doedsbo.skattepliktTilSvalbard ikkeEr oppgitt))
                    }

                hvis(
                    (harForekomstMedKommunenrSvalbard eller harForekomsterMedLandkodeSvalbard) og
                        harIkkeSkattepliktTilSvalbard
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1,
                    )
                }
            }
    }

