sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harSkattepliktTilSvalbard = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .hvorMinstEnForekomst { (personligSkattesubjekt.skattepliktTilSvalbard erLik "global") eller (doedsbo.skattepliktTilSvalbard erLik "global") }

        val eiendommerMedUtleie = skattemelding
           .forForekomsterAv{fastEiendom}    
           .filtrer{eiendomsforekomster ->
                eiendomsforekomster
                  .forForekomsterAv{fastEiendomSomUtleieobjekt}
                  .hvorMinstEnForekomst { (nettoLeieinntektForAndel.beloep er oppgitt) eller 
                                          (bruttoLeieinntektVedRegnskapsbehandletUtleie.beloep er oppgitt) eller
                                          (bruttoLeieinntektVedSjablongbehandletUtleie.beloep er oppgitt)}
           }
           .verdierFor{fastsatt(sergEiendomsidentifikator)}

        // Kontrollen gir utslag dersom skattyter har globalskatteplikt på Svalbard
        // og har primærbolig i Norge, 
        // men ikke har oppgitt utleie
        // kontrollen gir merknad i SME
      skattemelding
        .fokusområde("Fokusområde")
        .forForekomsterAv{fastEiendom} 
        .filtrer{((fastsatt(landkodeBeliggenhet) erLik "NO") eller (fastsatt(landkodeBeliggenhet) ikkeEr oppgitt)) og
                 (fastsatt(sergEiendomsidentifikator).finnesIkkeBlant(eiendommerMedUtleie))}
        .forForekomsterAv{fastEiendomSomFormuesobjekt}
        .utslagFor {

          val harPrimaerboligUtenUtleie = hvorMinstEnForekomst{boligbruk erLik "primaerbolig"}

          val harPrimaerboligFlerboligbyggUtenUtleie = forForekomsterAv{useksjonertBoenhet}
             .hvorMinstEnForekomst{boligbruk erLik "primaerbolig"}                   

          hvis(harSkattepliktTilSvalbard og
               (harPrimaerboligUtenUtleie eller harPrimaerboligFlerboligbyggUtenUtleie)
              ) {
            betjeningsstrategiSettesTil(ikke, merknadTips)
          }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag dersom skattepliktig har fradrag for svindel og
        //har svart Ja på spørsmål om det var i god tro og
        //har svart Nei på spørsmål om hen kan få investeringen tilbake på et senere tidspunkt og
        //har svart Ja på spørsmål om hen har forsøkt å få svindelen tilbakebetalt og
        //har svart Ja på spørsmål om svindelen er politanmeldt og
        // skal ikke gi utslag hvis skattepliktig har lagt ved dokumentasjon
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv{tapVedSvindel}
            .utslagFor {
                val harTapVedSvindel = hvorMinstEnForekomst {
                      (fastsatt(fradragsberettigetBeloepTaptVedSvindel.fradragsberettigetBeloep.beloep)er oppgitt)
                }
                 val vilkaarErOppfylt = hvorMinstEnForekomst {
                        ((investeringGjortIGodTro erLik true) og (svindelErPolitianmeldt erLik true) og
                         (erGjortForsoekPaaAaFaaTilbakeSvindletBeloep erLik true) og (harMulighetForTilbakefoeringAvSvindletBeloep erLik false))
                 }

             val harVedlegg = hvorMinstEnForekomst{fradragsberettigetBeloepTaptVedSvindel.beloepUtenHensynTilValgtPrioritertFradragstype.beloepIValuta har vedleggPåFelt}

                hvis(harTapVedSvindel og vilkaarErOppfylt og ikke(harVedlegg))
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon
                    )
                }
            }
        }

