sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        //gir utslag dersom skattepliktig egenfastsetter tap på svindel som er større enn investert beløp
      skattemelding
         .utslagFor {

          val sumTapVedSvindel = forForekomsterAv{tapVedSvindel}
              .summer {
                (fastsatt(fradragsberettigetBeloepTaptVedSvindel.fradragsberettigetBeloep.beloep) -
                    grunnlag(fradragsberettigetBeloepTaptVedSvindel.fradragsberettigetBeloep.beloep)) }

          val sumInvestertBeloep = forForekomsterAv{tapVedSvindel}
              .summer {
                (fastsatt(opprinneligInvesteringsbeloep.beloep) -
                    grunnlag(opprinneligInvesteringsbeloep.beloep)) }

          val sumTapMotInvestert = sumTapVedSvindel - sumInvestertBeloep

            hvis(sumTapMotInvestert erStørreEnn 0)
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harUtslagPaaKontroll = utslagPåKontroll("SÆRFRADRAG_ERVERVSEVNE_TRYGDET")

        val sumBankInnskudd = skattemelding
           .forForekomsterAv{konto}
           .summer{fastsatt(innskudd.beloep)}

        val sumAlminneligInntekt = skattemelding
           .forForekomsterAv{summertSkattegrunnlagForVisning}
           .forForekomsterAv{sumInntekterIAlminneligInntektFoerFordelingsfradrag}
           .summer { fastsatt(beloep) }

        // kontrollen gir utslag og merknad dersom skattepliktig har krevd særfradrag ervervsevne
        // og samlet bankinnskudd > 100 000 eller sum alminnelig inntekt > 500 000
