       skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv{saerfradragForLettereNedsattErvervsevne}
            .utslagFor {
                val kreverFradragForErvervsevne = hvorMinstEnForekomst{kreverSaerfradrag erLik true}

                hvis(ikke(harUtslagPaaKontroll) og
                     kreverFradragForErvervsevne og
                    ((sumBankInnskudd erStørreEnn 100_000) eller (sumAlminneligInntekt erStørreEnn 500_000))
                    )
              {
                    betjeningsstrategiSettesTil(
                        ikke, merknadBekreft, avhukingsboks)
                }
            }
        }

