            val harGjeldsrenterAnnenGjeld = forForekomsterAv{annenGjeld}
               .hvorMinstEnForekomst{(gjeldsbeloep.beloep erStørreEnn 0) og (gjeldsbeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")}

            val harGjeldsrenterInkassoselskapOgBetalingsformidler = forForekomsterAv{inkassoselskapOgBetalingsformidler}
               .hvorMinstEnForekomst{(paaloepteRenter.fradragsberettigetBeloep.beloep erStørreEnn 0) og (paaloepteRenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")}

            val harGjeldsrenterKonto = forForekomsterAv{konto}
               .hvorMinstEnForekomst{((paaloepteRenter.fradragsberettigetBeloep.beloep erStørreEnn 0) og (paaloepteRenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")) eller
                                     ((betalteMisligholdteRenter.beloep erStørreEnn 0) og (betalteMisligholdteRenter.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt"))}

            val harGjeldsrenterSkyldigRestskatt = forForekomsterAv{skyldigRestskatt}
               .hvorMinstEnForekomst{(betalteForsinkelsesrenter.fradragsberettigetBeloep.beloep erStørreEnn 0) og (betalteForsinkelsesrenter.fradragsberettigetBeloep.trygdeEllerSkattemessigUnntak erUlik "skattefriUtenTrygdeavgiftsplikt")}            


            hvis(harGjeldsrenterAnnenGjeld eller harGjeldsrenterInkassoselskapOgBetalingsformidler eller harGjeldsrenterKonto eller harGjeldsrenterSkyldigRestskatt) 
              {
                betjeningsstrategiSettesTil(ikke, merknadInformasjon)
            }
        }
        }

