                val realisertTapDeltakerfastsetting =
                    forForekomsterAv{realisasjonAvAndelISelskapMedDeltakerfastsetting}
                        .summer { skattepliktigTapVedRealisasjon.beloep }

                val latentGevinsDeltakerfastsetting = summer{latentGevinstVedRealisasjonAvAndel}

                val latentTapDeltakerfastsetting = summer{latentTapVedRealisasjonAvAndel}

             hvis(
                    ((realisertGevinsDeltakerfastsetting erStørreEnn 0) og (latentGevinsDeltakerfastsetting erLik 0)) eller
                        ((realisertTapDeltakerfastsetting erStørreEnn 0) og (latentTapDeltakerfastsetting erLik 0))
