        // Skal gi utslag hvis det er produsert UV av type "Utleie sekundærbolig på "skattyter 
         skattemelding
             .utslagFor {

             val harSjablonbehandletUtleie = forForekomsterAv{
                  grupper(sjablongbehandletUtleie,
