                }
                 .hvorMinstEnForekomst {fastsatt(bruttoLeieinntektVedRegnskapsbehandletUtleie.beloep) erStørreEnn 0}

             hvis(harUtvidetVeiledningPaaUtleie og
                     ikke(harSjablonbehandletUtleie) og
                     ikke(harRegnskapsbehandletUtleie) og
                     ikke(harUtleidFjoraaret)
