        // brukes for å styre om den skal vises eller ikke
        utvidetVeiledning
            .forForekomsterAv { utvidetveiledning }
            .utslagFor {
                val harFormueIFjor= forForekomsterAv{virtuellEiendel}
                   .hvorMinstEnForekomst{fastsatt(navn).finnesBlant(navnSomHarFormuesverdiVirtuellValutaIFjor)}

                val harUVAvGittType = forForekomsterAv { metadata }
                      .hvorMinstEnForekomst { type erLik "VIRTUELL_EIENDEL"}

                hvis(harFormueIFjor og 
                     harUVAvGittType og
                     (antallEgenfastsatteVirtuelleEiendeler erMindreEnn 2))
                {
                    betjeningsstrategiSettesTil(
                        ikke, visUtvidetVeiledning
                    )
                }
            }
    }

